import React, { useCallback, useEffect, useRef, useState } from "react";
import Webcam from "react-webcam";
import Quagga from "@ericblade/quagga2";
import "./SmartCapture.css";

type CaptureResult =
  | { kind: "barcode"; code: string; format?: string }
  | { kind: "image"; dataUrl: string; file?: File | null };

type Props = {
  onResult: (result: CaptureResult) => void;
  startingMode?: "barcode" | "camera" | "upload";
  width?: number;
  height?: number;
  onClose?: () => void;
};

const SmartCapture: React.FC<Props> = ({
  onResult,
  startingMode = "barcode",
  width = 640,
  height = 480,
  onClose,
}) => {
  const [mode, setMode] = useState<"barcode" | "camera" | "upload">(startingMode);
  const webcamRef = useRef<Webcam>(null);
  const quaggaStarted = useRef(false);
  const videoContainerRef = useRef<HTMLDivElement>(null);

  // ---------- BARCODE (Quagga) ----------
  const startQuagga = useCallback(async () => {
    if (quaggaStarted.current || mode !== "barcode") return;
    quaggaStarted.current = true;

    // Choose environment camera when available
    const constraints = {
      facingMode: { ideal: "environment" as const },
      width: { ideal: width },
      height: { ideal: height },
    };

    Quagga.init(
      {
        inputStream: {
          type: "LiveStream",
          target: videoContainerRef.current!,
          constraints,
          area: { top: "20%", right: "10%", left: "10%", bottom: "20%" }, // scan box
        },
        decoder: {
          readers: [
            "ean_reader",
            "ean_8_reader",
            "upc_reader",
            "upc_e_reader",
            "code_128_reader",
          ],
        },
        locate: true,
      },
      (err) => {
        if (err) {
          console.error("Quagga init error:", err);
          quaggaStarted.current = false;
          return;
        }
        Quagga.start();
      }
    );

    Quagga.onDetected((data) => {
      const code = data?.codeResult?.code;
      if (!code) return;
      onResult({ kind: "barcode", code, format: data.codeResult.format });
      // Optional: stop after first detection
      Quagga.stop();
      quaggaStarted.current = false;
    });
  }, [mode, onResult, width, height]);

  const stopQuagga = useCallback(() => {
    if (quaggaStarted.current) {
      try {
        Quagga.stop();
      } catch {}
      quaggaStarted.current = false;
    }
  }, []);

  // Manage scanner lifecycle on mode change
  useEffect(() => {
    if (mode === "barcode") startQuagga();
    else stopQuagga();
    return () => stopQuagga();
  }, [mode, startQuagga, stopQuagga]);

  // ---------- CAMERA (React Webcam) ----------
  const capturePhoto = () => {
    const shot = webcamRef.current?.getScreenshot();
    if (shot) onResult({ kind: "image", dataUrl: shot, file: null });
  };

  // ---------- UPLOAD ----------
  const onFile = async (file: File) => {
    const dataUrl = await fileToDataURL(file);
    onResult({ kind: "image", dataUrl, file });
  };

  return (
    <div className="smart-capture">
      {/* Close button */}
      {onClose && (
        <div className="sc-header">
          <button onClick={onClose} className="sc-close" aria-label="Close">
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
              <path d="M18 6L6 18M6 6L18 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
            </svg>
          </button>
        </div>
      )}

      <div className="sc-tabs" role="tablist" aria-label="Capture options">
        <button
          role="tab"
          aria-selected={mode === "barcode"}
          className={mode === "barcode" ? "active" : ""}
          onClick={() => setMode("barcode")}
        >
          📱 Scan Barcode
        </button>
        <button
          role="tab"
          aria-selected={mode === "camera"}
          className={mode === "camera" ? "active" : ""}
          onClick={() => setMode("camera")}
        >
          📷 Take Photo
        </button>
        <button
          role="tab"
          aria-selected={mode === "upload"}
          className={mode === "upload" ? "active" : ""}
          onClick={() => setMode("upload")}
        >
          📁 Upload Photo
        </button>
      </div>

      <div className="sc-panel">
        {mode === "barcode" && (
          <div className="sc-mode-info">
            <h3>Scan Product Barcode</h3>
            <p>Point your camera at a product barcode (UPC/EAN) to get nutrition information</p>
            <div
              ref={videoContainerRef}
              className="sc-video"
              style={{ width, height, position: "relative" }}
            >
              {/* scan box overlay */}
              <div className="sc-box" aria-hidden />
            </div>
          </div>
        )}

        {mode === "camera" && (
          <div className="sc-mode-info">
            <h3>Take Food Photo</h3>
            <p>Capture a photo of your food for AI nutrition analysis</p>
            <div className="sc-video" style={{ width, height, position: "relative" }}>
              <Webcam
                ref={webcamRef}
                audio={false}
                screenshotFormat="image/jpeg"
                videoConstraints={{ facingMode: "environment" }}
                style={{ width: "100%", height: "100%", borderRadius: 12 }}
              />
              <button className="sc-action" onClick={capturePhoto} aria-label="Capture photo">
                📸 Capture
              </button>
            </div>
          </div>
        )}

        {mode === "upload" && (
          <div className="sc-mode-info">
            <h3>Upload Food Photo</h3>
            <p>Select a photo from your device for nutrition analysis</p>
            <label className="sc-upload">
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  const f = e.target.files?.[0];
                  if (f) onFile(f);
                }}
              />
              <div className="sc-upload-content">
                <svg width="48" height="48" viewBox="0 0 24 24" fill="none" stroke="currentColor">
                  <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/>
                  <polyline points="7,10 12,15 17,10"/>
                  <line x1="12" y1="15" x2="12" y2="3"/>
                </svg>
                <span>Choose image file...</span>
              </div>
            </label>
          </div>
        )}
      </div>
    </div>
  );
};

export default SmartCapture;

// ---------- helpers ----------
function fileToDataURL(file: File): Promise<string> {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = () => resolve(String(reader.result));
    reader.onerror = reject;
    reader.readAsDataURL(file);
  });
}
