import React, { useState, useEffect } from 'react';
import { wihyAPI } from '../services/wihyAPI';
import ChatWidget from './ChatWidget';
import NutritionChart from './NutritionChart';
import NovaChart from './NovaChart';
import ShoppingListManager from './ShoppingListManager';
import HealthInsights from './HealthInsights';
import QuickActions from './QuickActions';

interface HealthDashboardProps {
  searchQuery?: string;
  searchResponse?: any;
  apiResponse?: any;
}

interface DashboardState {
  activeTab: 'overview' | 'nutrition' | 'shopping' | 'insights' | 'chat';
  chartData: any;
  nutritionData: any;
  healthInsights: any;
  shoppingList: any[];
  isLoading: boolean;
  error: string | null;
}

const HealthDashboard: React.FC<HealthDashboardProps> = ({ 
  searchQuery, 
  searchResponse, 
  apiResponse 
}) => {
  const [state, setState] = useState<DashboardState>({
    activeTab: 'overview',
    chartData: null,
    nutritionData: null,
    healthInsights: null,
    shoppingList: [],
    isLoading: false,
    error: null
  });

  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (apiResponse) {
      processApiResponse(apiResponse);
    }
  }, [apiResponse]);

  const processApiResponse = (response: any) => {
    if ('success' in response && 'data' in response && response.data) {
      const data = response.data;
      
      setState(prev => ({
        ...prev,
        chartData: data.charts_data,
        nutritionData: data.nutrition_data,
        healthInsights: data.health_insights,
        error: null
      }));
    }
  };

  const tabs = [
    { id: 'overview', label: 'Overview', icon: 'ðŸ“Š' },
    { id: 'nutrition', label: 'Nutrition', icon: 'ðŸ¥—' },
    { id: 'shopping', label: 'Shopping', icon: 'ðŸ›’' },
    { id: 'insights', label: 'Insights', icon: 'ðŸ’¡' },
    { id: 'chat', label: 'Chat', icon: 'ðŸ’¬' }
  ] as const;

  const renderTabContent = () => {
    switch (state.activeTab) {
      case 'overview':
        return <OverviewTab {...state} />;
      case 'nutrition':
        return <NutritionTab {...state} />;
      case 'shopping':
        return <ShoppingTab {...state} setState={setState} />;
      case 'insights':
        return <InsightsTab {...state} />;
      case 'chat':
        return (
          <ChatWidget
            isOpen={true}
            onClose={() => setState(prev => ({ ...prev, activeTab: 'overview' }))}
            searchQuery={searchQuery}
            searchResponse={searchResponse}
            inline={true}
          />
        );
      default:
        return <OverviewTab {...state} />;
    }
  };

  return (
    <div style={{
      display: 'flex',
      flexDirection: isMobile ? 'column' : 'row',
      height: isMobile ? 'auto' : '100vh',
      maxHeight: isMobile ? 'none' : '100vh',
      overflow: 'hidden',
      backgroundColor: '#f8fafc'
    }}>
      {/* Sidebar Navigation */}
      <div style={{
        width: isMobile ? '100%' : '250px',
        backgroundColor: 'white',
        borderRight: isMobile ? 'none' : '1px solid #e5e7eb',
        borderBottom: isMobile ? '1px solid #e5e7eb' : 'none',
        padding: '16px',
        overflowY: 'auto'
      }}>
        <div style={{
          fontSize: '18px',
          fontWeight: '600',
          color: '#1e293b',
          marginBottom: '20px',
          textAlign: 'center'
        }}>
          Health Dashboard
        </div>
        
        <div style={{
          display: isMobile ? 'flex' : 'block',
          gap: isMobile ? '8px' : '0',
          overflowX: isMobile ? 'auto' : 'visible'
        }}>
          {tabs.map(tab => (
            <button
              key={tab.id}
              onClick={() => setState(prev => ({ ...prev, activeTab: tab.id }))}
              style={{
                width: isMobile ? 'auto' : '100%',
                padding: '12px 16px',
                margin: isMobile ? '0' : '0 0 8px 0',
                border: 'none',
                borderRadius: '8px',
                backgroundColor: state.activeTab === tab.id ? '#3b82f6' : 'transparent',
                color: state.activeTab === tab.id ? 'white' : '#64748b',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
                fontSize: '14px',
                fontWeight: '500',
                transition: 'all 0.2s',
                whiteSpace: 'nowrap'
              }}
            >
              <span>{tab.icon}</span>
              <span>{tab.label}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div style={{
        flex: 1,
        padding: '20px',
        overflowY: 'auto',
        overflowX: 'hidden',
        minHeight: isMobile ? '400px' : 0
      }}>
        {renderTabContent()}
      </div>
    </div>
  );
};

// Overview Tab Component
const OverviewTab: React.FC<DashboardState> = ({ 
  chartData, 
  nutritionData, 
  healthInsights, 
  isLoading 
}) => {
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth <= 768);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div style={{
      display: 'grid',
      gridTemplateColumns: isMobile ? '1fr' : 'repeat(auto-fit, minmax(300px, 1fr))',
      gap: '20px',
      height: 'fit-content'
    }}>
      {/* Quick Stats */}
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '12px',
        border: '1px solid #e5e7eb'
      }}>
        <h3 style={{ margin: '0 0 16px 0', color: '#1e293b' }}>Quick Stats</h3>
        <QuickActions />
      </div>

      {/* Chart Data */}
      {chartData && (
        <div style={{
          backgroundColor: 'white',
          padding: '20px',
          borderRadius: '12px',
          border: '1px solid #e5e7eb'
        }}>
          <h3 style={{ margin: '0 0 16px 0', color: '#1e293b' }}>Analysis Charts</h3>
          <div style={{ height: '250px' }}>
            {chartData.nutrition_breakdown && (
              <NutritionChart apiResponse={{ data: { charts_data: chartData } }} />
            )}
            {chartData.health_quality && (
              <NovaChart apiResponse={{ data: { charts_data: chartData } }} />
            )}
          </div>
        </div>
      )}

      {/* Health Insights */}
      {healthInsights && (
        <div style={{
          backgroundColor: 'white',
          padding: '20px',
          borderRadius: '12px',
          border: '1px solid #e5e7eb'
        }}>
          <HealthInsights insights={healthInsights} />
        </div>
      )}
    </div>
  );
};

// Nutrition Tab Component
const NutritionTab: React.FC<DashboardState> = ({ 
  nutritionData, 
  chartData 
}) => {
  return (
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
      gap: '20px'
    }}>
      <div style={{
        backgroundColor: 'white',
        padding: '20px',
        borderRadius: '12px',
        border: '1px solid #e5e7eb'
      }}>
        <h3 style={{ margin: '0 0 16px 0', color: '#1e293b' }}>Nutrition Analysis</h3>
        {nutritionData ? (
          <pre style={{ 
            fontSize: '12px', 
            backgroundColor: '#f8fafc', 
            padding: '12px', 
            borderRadius: '6px',
            overflow: 'auto'
          }}>
            {JSON.stringify(nutritionData, null, 2)}
          </pre>
        ) : (
          <p style={{ color: '#64748b' }}>No nutrition data available</p>
        )}
      </div>
      
      {chartData?.nutrition_breakdown && (
        <div style={{
          backgroundColor: 'white',
          padding: '20px',
          borderRadius: '12px',
          border: '1px solid #e5e7eb'
        }}>
          <h3 style={{ margin: '0 0 16px 0', color: '#1e293b' }}>Nutrition Breakdown</h3>
          <div style={{ height: '300px' }}>
            <NutritionChart apiResponse={{ data: { charts_data: chartData } }} />
          </div>
        </div>
      )}
    </div>
  );
};

