import React, { useState } from 'react';
import ActivityChart from './ActivityChart';
import DopamineChart from './DopamineChart';
import HealthRiskChart from './HealthRiskChart';
import '../../styles/charts.css';

interface HealthWidgetsProps {
  showCharts?: boolean;
}

const HealthWidgets: React.FC<HealthWidgetsProps> = ({ showCharts = false }) => {
  const [selectedWidget, setSelectedWidget] = useState<'activity' | 'dopamine' | 'risk'>('activity');

  if (!showCharts) {
    return null;
  }

  return (
    <div style={{
      position: 'fixed',
      bottom: '20px',
      right: '20px',
      width: '350px',
      backgroundColor: 'white',
      borderRadius: '12px',
      boxShadow: '0 4px 20px rgba(0, 0, 0, 0.15)',
      border: '1px solid #e5e7eb',
      zIndex: 100,
      maxHeight: '400px',
      overflow: 'hidden'
    }}>
      {/* Widget Header */}
      <div style={{
        padding: '16px',
        borderBottom: '1px solid #e5e7eb',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h3 style={{
          margin: 0,
          fontSize: '16px',
          fontWeight: '600',
          color: '#374151'
        }}>
          Health Insights
        </h3>
        
        {/* Widget Selection */}
        <div style={{ display: 'flex', gap: '4px' }}>
          {[
            { key: 'activity', icon: '🏃', title: 'Activity' },
            { key: 'dopamine', icon: '🧠', title: 'Dopamine' },
            { key: 'risk', icon: '⚠️', title: 'Risk' }
          ].map((widget) => (
            <button
              key={widget.key}
              onClick={() => setSelectedWidget(widget.key as any)}
              style={{
                padding: '6px 8px',
                border: '1px solid #d1d5db',
                background: selectedWidget === widget.key ? '#3b82f6' : 'white',
                color: selectedWidget === widget.key ? 'white' : '#374151',
                borderRadius: '6px',
                fontSize: '12px',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                gap: '4px'
              }}
              title={widget.title}
            >
              {widget.icon}
            </button>
          ))}
        </div>
      </div>

      {/* Widget Content */}
      <div style={{ padding: '16px', height: '280px' }}>
        {selectedWidget === 'activity' && (
          <div>
            <h4 style={{ margin: '0 0 12px 0', fontSize: '14px', color: '#6b7280' }}>
              Weekly Activity
            </h4>
            <ActivityChart period="week" chartType="steps" />
          </div>
        )}
        
        {selectedWidget === 'dopamine' && (
          <div>
            <h4 style={{ margin: '0 0 12px 0', fontSize: '14px', color: '#6b7280' }}>
              Dopamine Levels
            </h4>
            <DopamineChart period="week" chartType="levels" />
          </div>
        )}
        
        {selectedWidget === 'risk' && (
          <div>
            <h4 style={{ margin: '0 0 12px 0', fontSize: '14px', color: '#6b7280' }}>
              Health Risk Factors
            </h4>
            <HealthRiskChart period="week" chartType="risk-factors" />
          </div>
        )}
      </div>
    </div>
  );
};

export default HealthWidgets;
