                      <span style={{ fontSize: '14px', fontWeight: '500', color: '#1f2937' }}>6.2/8 km</span>
                    </div>
                    <div className="progress-bar">
                      <div className="progress-fill" style={{ backgroundColor: '#3b82f6', width: '78%' }}></div>
                    </div>
                  </div>
                  <div className="wihy-btn-wrapper">
                    <button className="analyze-wihy-btn">
                      Analyze with WiHy
                    </button>
                  </div>
                </div>

                {/* Nutrition */}
                <div className="chart-section-card">
                  <h3 className="chart-section-title">
                    🥗 Nutrition
                  </h3>
                  <div className="chart-section-tabs">
                    <button className="chart-tab active">
                      Macro Breakdown
                    </button>
                    <button className="chart-tab inactive">
                      Micronutrients
                    </button>
                  </div>
                  <div className="wihy-btn-wrapper">
                    <button className="analyze-wihy-btn">
                      Analyze with WiHy
                    </button>
                  </div>
                </div>

                {/* Sleep */}
                <div className="chart-section-card">
                  <h3 className="chart-section-title">
                    💤 Sleep
                  </h3>
                  <div className="chart-section-tabs">
                    <button className="chart-tab active">
                      Duration & Quality
                    </button>
                    <button className="chart-tab inactive">
                      Quality Distribution
                    </button>
                  </div>
                  <div className="wihy-btn-wrapper">
                    <button className="analyze-wihy-btn">
                      Analyze with WiHy
                    </button>
                  </div>
                </div>
              </div>

              {/* Quick Insights */}
              <div className="quick-insights-card">
                <h3 className="quick-insights-title">
                  📊 Quick Insights
                </h3>
                <div className="insights-grid">
                  <div className="insight-item">
                    <div className="insight-value" style={{ color: '#10b981' }}>85</div>
                    <div className="insight-label">Overall Health Score</div>
                  </div>
                  <div className="insight-item">
                    <div className="insight-value" style={{ color: '#3b82f6' }}>7.2h</div>
                    <div className="insight-label">Avg Sleep Quality</div>
                  </div>
                  <div className="insight-item">
                    <div className="insight-value" style={{ color: '#f59e0b' }}>8.4k</div>
