                  </div>
                  <div className="insight-item">
                    <div className="insight-value" style={{ color: '#ef4444' }}>5</div>
                    <div className="insight-label">Personal Bests This Month</div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
