import React, { useState, useRef, useEffect } from 'react';
import { wihyAPI } from '../services/wihyAPI';

interface ChatMessage {
  id: string;
  type: 'user' | 'assistant';
  message: string;
  timestamp: Date;
  context?: string;
}

interface ChatWidgetProps {
  isOpen: boolean;
  onClose: () => void;
  searchQuery?: string;
  searchResponse?: string;
  currentContext?: string; // Current dashboard section being viewed
  inline?: boolean; // Whether to render inline or as fixed overlay
}

const ChatWidget: React.FC<ChatWidgetProps> = ({ isOpen, onClose, searchQuery, searchResponse, currentContext, inline = false }) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Auto-scroll to bottom when new messages are added
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Initialize conversation with search results if provided
  useEffect(() => {
    if (searchQuery && searchResponse && messages.length === 0) {
      // Create a conversational summary instead of full response
      let conversationalSummary = searchResponse;
      
      // Extract key points and make it conversational
      if (searchResponse.includes('WIHY Health')) {
        const lines = searchResponse.split('\n').filter(line => line.trim());
        const keyPoints = lines
          .filter(line => line.includes('â€¢') || line.includes('-'))
          .slice(0, 2)
          .map(line => line.replace(/[â€¢-]/g, '').trim())
          .join('. ');
          
        conversationalSummary = keyPoints || 'I found some helpful information for you!';
        conversationalSummary += ' What specific aspect would you like to explore further?';
      }
      
      const initialMessages: ChatMessage[] = [
        {
          id: Date.now().toString() + '-user',
          type: 'user',
          message: searchQuery,
          timestamp: new Date()
        },
        {
          id: Date.now().toString() + '-assistant',
          type: 'assistant',
          message: conversationalSummary,
          timestamp: new Date()
        }
      ];
      setMessages(initialMessages);
    }
  }, [searchQuery, searchResponse, messages.length]);

  // Focus input when chat opens
  useEffect(() => {
    if (isOpen && inputRef.current) {
      setTimeout(() => inputRef.current?.focus(), 100);
    }
  }, [isOpen]);

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage: ChatMessage = {
      id: Date.now().toString() + '-user',
      type: 'user',
      content: inputValue.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      // Build conversation context from previous messages
      const conversationHistory = messages.slice(-4) // Last 4 messages for context
        .map(msg => `${msg.type === 'user' ? 'User' : 'AI'}: ${msg.content}`)
        .join('\n');
      
      // Create a focused query for conversational responses
      const contextualQuery = `Please provide a brief, conversational response (2-3 sentences max) to: ${userMessage.content}${conversationHistory ? `\n\nContext from our conversation: ${conversationHistory}` : ''}`;

      const response = await wihyAPI.askAnything({
        query: contextualQuery,
        user_context: {
          conversation_mode: true,
          response_style: 'concise',
          conversation_context: conversationHistory,
          current_context: currentContext,
          is_followup: messages.length > 0
        }
      });

      // Extract just the main response without full formatting
      let aiResponse = '';
      if ('success' in response && 'data' in response && response.data && 'response' in response.data) {
        const healthResp = response as any;
        aiResponse = healthResp.data.response;
        
        // Clean up the response to be more conversational
        aiResponse = aiResponse
          .replace(/ðŸ¥—.*?\*\*/g, '') // Remove emoji headers
          .replace(/\*\*.*?\*\*/g, '') // Remove bold formatting
          .replace(/ðŸ“‹.*?:/g, '') // Remove section headers
          .replace(/â€¢/g, '-') // Replace bullets
          .split('\n')
          .filter(line => line.trim() && !line.includes('Biblical') && !line.includes('Corinthians'))
          .slice(0, 3) // Take first 3 meaningful lines
          .join(' ')
          .trim();
          
        // If response is too long, truncate and add follow-up prompt
        if (aiResponse.length > 200) {
          aiResponse = aiResponse.substring(0, 200).trim() + '... What would you like to know more about?';
        }
      } else {
        aiResponse = wihyAPI.formatWihyResponse(response);
      }
      
      const aiMessage: ChatMessage = {
        id: Date.now().toString() + '-ai',
        type: 'ai',
        content: aiResponse,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Chat error:', error);
      const errorMessage: ChatMessage = {
        id: Date.now().toString() + '-ai',
        type: 'ai',
        content: "I'm having trouble connecting right now. Please try again in a moment.",
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };
  
  if (!isOpen) {
    return null;
  }

  return (
    <>
      {/* Just render the messages without outer container */}

      {/* Messages Area - render directly without container */}
      <div 
        style={{
          display: 'flex',
          flexDirection: 'column',
          gap: '12px',
          height: '300px', // Fixed height
          overflowY: 'auto', // Enable vertical scrolling
          overflowX: 'hidden', // Prevent horizontal scroll
          padding: '8px',
          border: '1px solid #e2e8f0',
          borderRadius: '8px',
          backgroundColor: '#fafafa',
          // Hide scrollbar on mobile
          WebkitOverflowScrolling: 'touch',
          scrollbarWidth: window.innerWidth <= 768 ? 'none' : 'thin',
          msOverflowStyle: window.innerWidth <= 768 ? 'none' : 'auto'
        }}
        className={window.innerWidth <= 768 ? '' : 'custom-scrollbar'}>
        {messages.length === 0 ? (
          <div style={{
            textAlign: 'center',
            padding: '40px 20px',
            color: '#64748b'
          }}>
            <div style={{
              fontSize: '48px',
              marginBottom: '16px',
              opacity: 0.3
            }}>
              ðŸ’¬
            </div>
            <div style={{
              fontSize: '14px',
              fontWeight: '500',
              marginBottom: '8px'
            }}>
              Start a health conversation
            </div>
            <div style={{
              fontSize: '12px',
              color: '#94a3b8'
            }}>
              Ask any health question to begin learning
            </div>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              style={{
                display: 'flex',
                justifyContent: message.type === 'user' ? 'flex-end' : 'flex-start',
                marginBottom: '8px'
              }}
            >
              <div
                style={{
                  maxWidth: '80%',
                  padding: '12px 16px',
                  borderRadius: '18px',
                  backgroundColor: message.type === 'user' ? '#3b82f6' : '#f1f5f9',
                  color: message.type === 'user' ? 'white' : '#334155',
                  fontSize: '14px',
                  lineHeight: '1.5',
                  whiteSpace: 'pre-wrap',
                  wordWrap: 'break-word'
                }}
              >
                {message.content}
              </div>
            </div>
          ))
        )}
        
        {isLoading && (
          <div style={{ display: 'flex', justifyContent: 'flex-start' }}>
            <div style={{
              padding: '12px 16px',
              borderRadius: '18px',
              backgroundColor: '#f1f5f9',
              color: '#64748b',
              fontSize: '14px'
            }}>
              <div style={{
                display: 'flex',
                gap: '4px',
                alignItems: 'center'
              }}>
                <span>Thinking</span>
                <div style={{
                  display: 'inline-flex',
                  gap: '2px'
                }}>
                  <div style={{
                    width: '4px',
                    height: '4px',
                    borderRadius: '50%',
                    backgroundColor: '#64748b',
                    animation: 'pulse 1.4s ease-in-out infinite both',
                    animationDelay: '0s'
                  }}></div>
                  <div style={{
                    width: '4px',
                    height: '4px',
                    borderRadius: '50%',
                    backgroundColor: '#64748b',
                    animation: 'pulse 1.4s ease-in-out infinite both',
                    animationDelay: '0.2s'
                  }}></div>
                  <div style={{
                    width: '4px',
                    height: '4px',
                    borderRadius: '50%',
                    backgroundColor: '#64748b',
                    animation: 'pulse 1.4s ease-in-out infinite both',
                    animationDelay: '0.4s'
                  }}></div>
                </div>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div style={{
        display: 'flex',
        gap: '8px',
        alignItems: 'center',
        marginTop: '16px'
      }}>
        <input
          ref={inputRef}
          type="text"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Ask a follow-up question..."
          disabled={isLoading}
          style={{
            flex: 1,
            padding: '8px 12px',
            border: '1px solid #d1d5db',
            borderRadius: '16px',
            fontSize: '14px',
            outline: 'none',
            backgroundColor: isLoading ? '#f9fafb' : 'white'
          }}
        />
        <button
          onClick={handleSendMessage}
          disabled={!inputValue.trim() || isLoading}
          style={{
            padding: '8px 16px',
            backgroundColor: (!inputValue.trim() || isLoading) ? '#d1d5db' : '#3b82f6',
            color: 'white',
            border: 'none',
            borderRadius: '16px',
            cursor: (!inputValue.trim() || isLoading) ? 'not-allowed' : 'pointer',
            fontSize: '14px',
            fontWeight: '500'
          }}
        >
          Send
        </button>
      </div>

    </>
  );
};

export default ChatWidget;
