import axios, { AxiosError } from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

export interface NewsArticle {
  id: string;
  title: string;
  summary: string;
  url: string;
  source: string;
  domain: string;
  category: string;
  publishedDate: string;
  relevanceScore: number;
  tags: string[];
}

export interface NewsFeedResponse {
  success: boolean;
  message?: string;
  articles?: NewsArticle[];
  categories?: Record<string, NewsArticle[]>;
  lastUpdated?: string;
  count?: number; // Total number of articles
}

export const TRUSTED_DOMAINS: Record<string, number> = {
  'nih.gov': 1,
  'ncbi.nlm.nih.gov': 1,  // PubMed
  'who.int': 1,
  'cdc.gov': 1,
  'fda.gov': 1,
  'usda.gov': 1,
  'jamanetwork.com': 1,
  'nejm.org': 1,
  'thelancet.com': 1,
  'bmj.com': 1,
  'nature.com': 1,
  'science.org': 1,
  'mayoclinic.org': 1,
  'harvard.edu': 1,
  'stanford.edu': 1,
  'clevelandclinic.org': 1,
  'uptodate.com': 1,
  'cochranelibrary.com': 1, // Added Cochrane Library
  'pubmed.ncbi.nlm.nih.gov': 1, // Alternative PubMed URL
  'medlineplus.gov': 1, // MedlinePlus
};

export const NEWS_CATEGORIES = {
  NUTRITION: 'Nutrition & Diet',
  MEDICAL: 'Medical Research',
  PUBLIC_HEALTH: 'Public Health',
  CLINICAL: 'Clinical Studies',
  PREVENTION: 'Disease Prevention',
  MENTAL_HEALTH: 'Mental Health',
  GENERAL: 'General Health'
};

// Helper function to check if error is an Axios error
const isAxiosError = (error: any): error is AxiosError => {
  return error && error.isAxiosError === true;
};

// Helper function to validate if a domain is trusted
export const isTrustedDomain = (url: string): boolean => {
  try {
    const domain = new URL(url).hostname.toLowerCase();
    return Object.keys(TRUSTED_DOMAINS).some(trustedDomain => 
      domain.includes(trustedDomain.toLowerCase())
    );
  } catch {
    return false;
  }
};

// Fetch categorized news feed
export const fetchNewsFeed = async (categories?: string[], limit?: number): Promise<NewsFeedResponse> => {
  try {
    console.log('🔍 Fetching news feed...', { categories, limit });
    
    const params = new URLSearchParams();
    if (categories && categories.length > 0) {
      params.append('categories', categories.join(','));
    }
    if (limit) {
      params.append('limit', limit.toString());
    }
    
    const response = await axios.get(`${API_URL}/news/feed?${params.toString()}`);
    console.log('✅ News feed response:', response.data);
    
    // Type assertion with validation
    const data = response.data as NewsFeedResponse;
    return {
      success: data.success ?? true,
      message: data.message,
      articles: data.articles || [],
      categories: data.categories || {},
      lastUpdated: data.lastUpdated,
      count: data.count
    };
  } catch (error) {
    console.error('❌ Error fetching news feed:', error);
    
    // Check if it's a network error or server error
    if (isAxiosError(error)) {
      const status = error.response?.status;
      const message = error.response?.data?.message || error.message;
      
      return {
        success: false,
        message: `Network error (${status}): ${message}`
      };
    }
    
    return {
      success: false,
      message: 'Error fetching news feed'
    };
  }
};

// Refresh news feed (triggers backend to fetch latest articles)
export const refreshNewsFeed = async (): Promise<NewsFeedResponse> => {
  try {
    console.log('🔄 Refreshing news feed...');
    
    const response = await axios.post(`${API_URL}/news/refresh`);
    console.log('✅ News feed refreshed:', response.data);
    
    // Type assertion with validation
    const data = response.data as NewsFeedResponse;
    return {
      success: data.success ?? true,
      message: data.message,
      articles: data.articles || [],
      categories: data.categories || {},
      lastUpdated: data.lastUpdated,
      count: data.count
    };
  } catch (error) {
    console.error('❌ Error refreshing news feed:', error);
    
    if (isAxiosError(error)) {
      const status = error.response?.status;
      const message = error.response?.data?.message || error.message;
      
      return {
        success: false,
        message: `Refresh failed (${status}): ${message}`
      };
    }
    
    return {
      success: false,
      message: 'Error refreshing news feed'
    };
  }
};

// Get articles by specific category
export const getArticlesByCategory = async (category: string, limit?: number): Promise<NewsFeedResponse> => {
  try {
    console.log('📂 Fetching articles for category:', category);
    
    const params = new URLSearchParams();
    if (limit) {
      params.append('limit', limit.toString());
    }
    
    const url = `${API_URL}/news/category/${encodeURIComponent(category)}${params.toString() ? '?' + params.toString() : ''}`;
    const response = await axios.get(url);
    
    console.log('✅ Category articles response:', response.data);
    
    // Type assertion with validation
    const data = response.data as NewsFeedResponse;
    return {
      success: data.success ?? true,
      message: data.message,
      articles: data.articles || [],
      categories: data.categories || {},
      lastUpdated: data.lastUpdated,
      count: data.count
    };
  } catch (error) {
    console.error('❌ Error fetching articles by category:', error);
    
    if (isAxiosError(error)) {
      const status = error.response?.status;
      const message = error.response?.data?.message || error.message;
      
      return {
        success: false,
        message: `Category fetch failed (${status}): ${message}`
      };
    }
    
    return {
      success: false,
      message: 'Error fetching articles by category'
    };
  }
};

// Search articles
export const searchNewsArticles = async (query: string, category?: string, limit?: number): Promise<NewsFeedResponse> => {
  try {
    console.log('🔎 Searching articles:', { query, category, limit });
    
    if (!query.trim()) {
      return {
        success: false,
        message: 'Search query cannot be empty'
      };
    }
    
    const params = new URLSearchParams({ q: query.trim() });
    if (category && category !== 'all') {
      params.append('category', category);
    }
    if (limit) {
      params.append('limit', limit.toString());
    }
    
    const response = await axios.get(`${API_URL}/news/search?${params.toString()}`);
    console.log('✅ Search results:', response.data);
    
    // Type assertion with validation
    const data = response.data as NewsFeedResponse;
    return {
      success: data.success ?? true,
      message: data.message,
      articles: data.articles || [],
      categories: data.categories || {},
      lastUpdated: data.lastUpdated,
      count: data.count
    };
  } catch (error) {
    console.error('❌ Error searching news articles:', error);
    
    if (isAxiosError(error)) {
      const status = error.response?.status;
      const message = error.response?.data?.message || error.message;
      
      return {
        success: false,
        message: `Search failed (${status}): ${message}`
      };
    }
    
    return {
      success: false,
      message: 'Error searching news articles'
    };
  }
};

// Get news feed stats
export const getNewsFeedStats = async (): Promise<{
  success: boolean;
  message?: string;
  stats?: {
    totalArticles: number;
    categoryCounts: Record<string, number>;
    lastUpdated: string;
    sourcesCounts: Record<string, number>;
  };
}> => {
  try {
    console.log('📊 Fetching news feed stats...');
    
    const response = await axios.get(`${API_URL}/news/stats`);
    console.log('✅ Stats response:', response.data);
    
    // Type assertion with validation
    const data = response.data as any;
    return {
      success: data.success ?? true,
      message: data.message,
      stats: data.stats
    };
  } catch (error) {
    console.error('❌ Error fetching stats:', error);
    
    return {
      success: false,
      message: 'Error fetching news feed statistics'
    };
  }
};

// Utility function to format articles for display
export const formatArticleForDisplay = (article: NewsArticle) => {
  return {
    ...article,
    formattedDate: formatTimeAgo(article.publishedDate),
    isTrusted: isTrustedDomain(article.url),
    relevancePercentage: Math.round(article.relevanceScore * 100),
    shortSummary: article.summary.length > 150 
      ? article.summary.substring(0, 150) + '...'
      : article.summary
  };
};

// Helper function to format time ago
export const formatTimeAgo = (dateString: string): string => {
  try {
    const now = new Date();
    const publishedDate = new Date(dateString);
    const diffInHours = Math.floor((now.getTime() - publishedDate.getTime()) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Just now';
    if (diffInHours < 24) return `${diffInHours}h ago`;
    
    const diffInDays = Math.floor(diffInHours / 24);
    if (diffInDays < 7) return `${diffInDays}d ago`;
    
    const diffInWeeks = Math.floor(diffInDays / 7);
    if (diffInWeeks < 4) return `${diffInWeeks}w ago`;
    
    const diffInMonths = Math.floor(diffInDays / 30);
    return `${diffInMonths}mo ago`;
  } catch {
    return 'Unknown';
  }
};

// Get available categories
export const getAvailableCategories = (): Array<{id: string, label: string}> => {
  return [
    { id: 'all', label: 'All Health News' },
    { id: NEWS_CATEGORIES.NUTRITION, label: 'Nutrition & Diet' },
    { id: NEWS_CATEGORIES.MEDICAL, label: 'Medical Research' },
    { id: NEWS_CATEGORIES.PUBLIC_HEALTH, label: 'Public Health' },
    { id: NEWS_CATEGORIES.CLINICAL, label: 'Clinical Studies' },
    { id: NEWS_CATEGORIES.PREVENTION, label: 'Disease Prevention' },
    { id: NEWS_CATEGORIES.MENTAL_HEALTH, label: 'Mental Health' },
    { id: NEWS_CATEGORIES.GENERAL, label: 'General Health' }
  ];
};
