import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import VHealthSearch from './VHealthSearch';
import SearchResults from './SearchResults';
import './styles/VHealthSearch.css';

// Create a wrapper component that can use navigation hooks
const AppContent: React.FC = () => {
  const [currentPage, setCurrentPage] = useState('search');
  const [searchResults, setSearchResults] = useState<any>(null);
  const [currentQuery, setCurrentQuery] = useState('');
  const [dataSource, setDataSource] = useState<'error' | 'openai' | 'local' | 'vnutrition'>('openai');
  const [citations, setCitations] = useState<any[]>([]);
  const navigate = useNavigate();
  const location = useLocation();

  // Handle URL changes
  useEffect(() => {
    const urlParams = new URLSearchParams(location.search);
    const query = urlParams.get('q');
    
    if (query && location.pathname === '/results') {
      setCurrentQuery(query);
      setCurrentPage('results');
    } else {
      setCurrentPage('search');
    }
  }, [location]);

  const handleBackToSearch = () => {
    setCurrentPage('search');
    setSearchResults(null);
    setCurrentQuery('');
    navigate('/');
  };

  const handleNewSearch = (newQuery: string) => {
    setCurrentQuery(newQuery);
    setCurrentPage('search');
    navigate('/');
  };

  return (
    <div className="App">
      {currentPage === 'search' && (
        <VHealthSearch />
      )}
      
      {currentPage === 'results' && (
        <SearchResults
          query={currentQuery}
          results={searchResults}
          onBackToSearch={handleBackToSearch}
          onNewSearch={handleNewSearch}
          isLoading={false}  // Add this missing prop
          dataSource={dataSource}
          citations={citations}
        />
      )}
    </div>
  );
};

// Main App component with Router wrapper
const App: React.FC = () => {
  return (
    <Router>
      <Routes>
        <Route path="/*" element={<AppContent />} />
      </Routes>
    </Router>
  );
};

export default App;
