import React, { useState, useEffect } from 'react';
import './VHealthSearch.css';
import SearchResults from './SearchResults';

const VHealthSearch: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [apiStatus, setApiStatus] = useState('Checking...');
  const [searchResults, setSearchResults] = useState<any>(null);

  // Check API status on component mount
  useEffect(() => {
    const checkApiStatus = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/health');
        if (response.ok) {
          const data = await response.json();
          setApiStatus(`✅ ${data.message}`);
        } else {
          setApiStatus('❌ API connection failed');
        }
      } catch (error) {
        setApiStatus('❌ Error connecting to API');
      }
    };

    checkApiStatus();
  }, []);

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;

    try {
      const response = await fetch('http://localhost:5000/api/analyze', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ query: searchQuery }),
      });

      const data = await response.json();
      console.log('Analysis result:', data);
      setSearchResults(data);
    } catch (error) {
      console.error('Search error:', error);
    }
  };

  return (
    <div className="google-style-app">
      <div className="landing-container">
        {/* API Status */}
        <div className="api-status">
          API Status: {apiStatus}
        </div>

        <main className="main-content">
          {/* Logo - "What is Healthy?" */}
          <div className="logo-container">
            <h1 className="what-is-healthy-logo">
              <span className="what">What</span>
              <span className="is">is</span>
              <span className="healthy">Healthy</span>
              <span className="question">?</span>
            </h1>
            <p className="tagline">AI-Powered Nutrition Intelligence</p>
          </div>

          {/* Search Box */}
          <div className="search-container">
            <div className="search-box">
              <div className="search-icon">🔍</div>
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
                placeholder="Enter any food or ingredient..."
                className="search-input"
                autoFocus
              />
              <div className="mic-icon">🎤</div>
            </div>
            
            <div className="search-buttons">
              <button 
                onClick={handleSearch}
                className="search-btn primary"
                disabled={!searchQuery.trim()}
              >
                Analyze Nutrition
              </button>
              <button 
                onClick={() => {
                  const healthyFoods = ['apple', 'broccoli', 'salmon', 'quinoa', 'blueberries'];
                  const randomFood = healthyFoods[Math.floor(Math.random() * healthyFoods.length)];
                  setSearchQuery(randomFood);
                }}
                className="search-btn secondary"
              >
                I'm Feeling Healthy
              </button>
            </div>
          </div>

          {/* Use SearchResults Component */}
          {searchResults && (
            <SearchResults results={searchResults} />
          )}
        </main>
      </div>
    </div>
  );
};

export default VHealthSearch;
