import React, { useRef, useEffect } from 'react';

interface RainbowButtonProps {
  children: React.ReactNode;
  onClick?: (e: React.MouseEvent<HTMLButtonElement>) => void;
  className?: string;
  size?: 'small' | 'medium' | 'large';
}

const RainbowButton: React.FC<RainbowButtonProps> = ({ 
  children, 
  onClick, 
  className = '', 
  size = 'medium' 
}) => {
  const buttonRef = useRef<HTMLButtonElement>(null);
  
  useEffect(() => {
    // Force animation to run by re-applying the animation properties
    const button = buttonRef.current;
    if (button) {
      // Reset animation to force it to restart
      button.style.animation = 'none';
      // Force reflow
      void button.offsetWidth;
      // Re-apply animation
      button.style.animation = 'rainbow-border-sweep 2.2s linear infinite';
    }
  }, []);
  
  // Determine size class
  const sizeClass = size === 'small' ? 'rainbow-btn-small' : 
                   size === 'large' ? 'rainbow-btn-large' : '';
  
  return (
    <button
      ref={buttonRef}
      className={`rainbow-btn ${sizeClass} ${className}`}
      onClick={onClick}
      style={{
        position: 'relative',
        display: 'inline-flex',
        alignItems: 'center',
        padding: size === 'small' ? '6px 12px' : size === 'large' ? '12px 24px' : '8px 16px',
        borderRadius: size === 'small' ? '12px' : size === 'large' ? '20px' : '16px',
        fontSize: size === 'small' ? '11px' : size === 'large' ? '16px' : '14px',
        fontWeight: 600,
        cursor: 'pointer',
        border: '2px solid transparent',
        color: '#1a73e8',
        whiteSpace: 'nowrap',
        gap: '4px',
        
        // Hardware acceleration
        transform: 'translateZ(0)',
        backfaceVisibility: 'hidden',
        
        // Critical animation properties
        animation: 'rainbow-border-sweep 2.2s linear infinite',
        backgroundImage: 'linear-gradient(#fff, #fff), linear-gradient(90deg, #fa5f06, #ffffff, #C0C0C0, #4cbb17, #1a73e8)',
        backgroundOrigin: 'border-box',
        backgroundClip: 'padding-box, border-box',
        backgroundSize: '100% 100%, 200% 100%',
        
        // Ensure animation always runs
        willChange: 'background-position',
      }}
    >
      {children}
    </button>
  );
};

export default RainbowButton;
