/* filepath: c:\vHealth\vhealth\client\src\SearchResults.tsx */
import React, { useState } from 'react';
import Header from './components/shared/components/Header';
import Spinner from './components/Spinner';

interface SearchResultsProps {
  query: string;
  results: any[];
  onBackToSearch: () => void;
  onNewSearch: (query: string) => Promise<void>;
  isLoading?: boolean;
  dataSource?: any;
  citations?: any;
}

const SearchResults: React.FC<SearchResultsProps> = ({ 
  query, 
  results, 
  onBackToSearch,
  onNewSearch,
  isLoading = false,
  dataSource,
  citations
}) => {
  const [isSearching, setIsSearching] = useState(false);

  const handleHeaderSearch = async (newQuery: string) => {
    if (newQuery.trim() && newQuery !== query) {
      setIsSearching(true);
      
      try {
        // Call the parent's onNewSearch which should handle the API call
        await onNewSearch(newQuery);
      } catch (error) {
        console.error('Header search error:', error);
      } finally {
        setIsSearching(false);
      }
    }
  };

  const handleLogoClick = () => {
    onBackToSearch();
  };

  return (
    <div className="results-page">
      <Header
        searchQuery={query}
        onSearchSubmit={handleHeaderSearch}
        onVoiceInput={() => {/* voice logic */}}
        onImageUpload={() => setIsUploadModalOpen(true)}
        onLogoClick={handleLogoClick}
        isListening={false}
        variant="results"
        showLogin={true}
      />

      {/* Show spinner when searching from header */}
      {(isLoading || isSearching) && (
        <Spinner
          overlay={true}
          title="Analyzing with AI..."
          subtitle="Processing your health query..."
          disableEsc={true}
        />
      )}
      
      <div className="results-container">
        {hasValidResults ? (
          <>
            <h1 className="results-header">Search Results for: "{query}"</h1>
            
            {/* ...existing results content... */}
          </>
        ) : (
          !isLoading && !isSearching && (
            <div className="no-results-message">
              <p>No results to display. Try searching for something else.</p>
            </div>
          )
        )}
      </div>

      {/* ...existing modals... */}
    </div>
  );
};

export default SearchResults;
