    // Handle chat service responses
    if (response.service_used === 'chat' && response.data.response) {
      // For now, the API is returning very brief responses like "AI Chat response to: what is healthy"
      // We should provide more helpful information to the user
      const briefResponse = response.data.response;
      if (briefResponse.includes('AI Chat response to:')) {
        // The API gave us a placeholder response, provide something more useful
        const query = response.data.query || 'your question';
        return `I received your question about "${query}" and I'm here to help! 

The WiHy AI system is currently processing health-related queries. While the response system is being optimized, I can help you with:

‚Ä¢ Understanding health and nutrition concepts
‚Ä¢ Providing general wellness guidance  
‚Ä¢ Explaining health data and metrics
‚Ä¢ Offering evidence-based health insights

What specific aspect of health would you like to explore further?`;
      }
      return briefResponse;
    }

    // Handle other response types
    if (response.data.response) {
      return response.data.response;
    }
    if (response.data.analysis) {
      return response.data.analysis;
    }
    if (response.data.training_status) {
      let message = `üîÑ ${response.data.training_status}`;
      if (response.data.available_models && response.data.available_models.length > 0) {
        message += `\n\nüìä Available models: ${response.data.available_models.join(', ')}`;
      }
      return message;
    }

    // Fallback - show the raw data in a readable format
    return `I received a response from the ${response.service_used} service. Here's what I found:\n\n${JSON.stringify(response.data, null, 2)}`;
  }

  /**
   * Format response for display - compatibility alias for formatWihyResponse
   */
  formatResponse(response) {
    return this.formatWihyResponse(response);
  }

  /**
   * Enable local development mode for testing
   */
  enableLocalDevelopment() {
    this.isLocalDevelopment = true;
    this.baseURL = 'http://localhost:8000/ask';
    console.log('%cüîß SWITCHED TO LOCAL DEVELOPMENT MODE', 'color: yellow; font-weight: bold; font-size: 14px;');
    console.log('%cAsk Endpoint: http://localhost:8000/ask', 'color: cyan;');
    console.log('%cScan Endpoint: http://localhost:8000/scan', 'color: cyan;');

    // Test connectivity
    this.testLocalConnectivity();
  }

  /**
   * Test connectivity to local development server
   */
  async testLocalConnectivity() {
    try {
      console.log('üîç TESTING LOCAL CONNECTIVITY:', {
        timestamp: new Date().toISOString(),
        endpoint: 'http://localhost:8000/ask'
      });
      const response = await fetch('http://localhost:8000/ask', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          query: 'test connectivity',
          user_context: {
            test: true
          },
          include_nutrition: false,
          include_biblical_wisdom: false,
          include_charts: false
        })
      });
      if (response.ok) {
        console.log('%c‚úÖ LOCAL SERVER IS RESPONDING', 'color: green; font-weight: bold;');
        console.log('üîç LOCAL SERVER RESPONSE:', {
          status: response.status,
          statusText: response.statusText,
          headers: {
            contentType: response.headers.get('content-type')
          }
        });
      } else {
        console.log('%c‚ö†Ô∏è LOCAL SERVER RESPONDED WITH ERROR', 'color: orange; font-weight: bold;');
        console.log('üîç LOCAL SERVER ERROR:', {
          status: response.status,
          statusText: response.statusText
        });
      }
    } catch (error) {
      console.log('%c‚ùå LOCAL SERVER NOT ACCESSIBLE', 'color: red; font-weight: bold;');
      console.log('üîç LOCAL SERVER ERROR:', {
        error: error instanceof Error ? error.message : String(error),
        suggestion: 'Make sure your local development server is running on http://localhost:8000'
      });
    }
  }

  /**
   * Get current endpoint information
   */
  getEndpointInfo() {
    return {
      isLocalDevelopment: this.isLocalDevelopment,
      baseURL: this.baseURL,
      askEndpoint: this.isLocalDevelopment ? 'http://localhost:8000/ask' : `${API_CONFIG.WIHY_API_URL}/ask`,
      scanEndpoint: this.isLocalDevelopment ? 'http://localhost:8000/scan' : `${API_CONFIG.WIHY_API_URL}/scan`
    };
  }
}

// Export a singleton instance
export const wihyAPI = new WihyAPIService();

// Add global access for testing in browser console
if (typeof window !== 'undefined') {
  window.wihyAPI = wihyAPI;
  window.enableLocalDev = () => wihyAPI.enableLocalDevelopment();
  window.getEndpoints = () => wihyAPI.getEndpointInfo();

  // Show help in console
  console.log('%cüõ†Ô∏è DEVELOPMENT HELPERS AVAILABLE:', 'color: lightblue; font-weight: bold;');
  console.log('%cwihyAPI.enableLocalDevelopment() - Switch to local development', 'color: lightblue;');
  console.log('%cwihyAPI.testLocalConnectivity() - Test local server connection', 'color: lightblue;');
  console.log('%cwihyAPI.getEndpointInfo() - Show current endpoint configuration', 'color: lightblue;');
  console.log('%cOr use shortcuts: enableLocalDev(), getEndpoints()', 'color: lightblue;');
}
export default wihyAPI;

