import React, { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './VHealthSearch.css';
import ImageUploadModal from './components/ImageUploadModal';
import MultiAuthLogin from './components/shared/components/MultiAuthLogin';
import { healthSearchService } from './services/healthSearchService';
import { searchCache } from './services/searchCache';
import { foodAnalysisService } from './components/foodAnalysisService';

const VHealthSearch: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [isUploadModalOpen, setIsUploadModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [imageError, setImageError] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('Searching...');
  
  const navigate = useNavigate();
  const searchInputRef = useRef<HTMLInputElement>(null);
  const recognitionRef = useRef<any>(null);

  const progressFromMessage = (msg: string) => {
    if (msg.includes('Initializing')) return 10;
    if (msg.includes('Checking cache')) return 25;
    if (msg.includes('Analyzing with AI')) return 55;
    if (msg.includes('Processing results')) return 75;
    if (msg.includes('Results ready') || msg.includes('Response ready')) return 95;

    if (msg.includes('Processing image')) return 35;
    if (msg.includes('Checking nutrition')) return 45;
    if (msg.includes('Analyzing nutrition')) return 60;
    if (msg.includes('Processing nutrition')) return 80;
    if (msg.includes('complete')) return 95;

    if (msg.includes('failed')) return 100; // will close shortly
    return 40; // default midpoint
  };

  const handleUserChange = (user: any) => {
    console.log('User changed:', user);
  };

  const handleSignIn = (user: any) => {
    console.log('User signed in:', user);
  };

  const handleSignOut = () => {
    console.log('User signed out');
  };

  const handleSearch = async () => {
    if (!searchQuery.trim() || isLoading) return;
    
    setIsLoading(true);
    setLoadingMessage('Initializing search...');
    
    try {
      // Always get fresh results from API first (skip cache for now to ensure fresh data)
      setLoadingMessage('Analyzing with AI...');
      console.log('🔍 Searching for:', searchQuery);
      
      try {
        const searchResults = await healthSearchService.searchHealthInfo(searchQuery);
        console.log('✅ Health search results received:', searchResults);
        console.log('Results type:', typeof searchResults);
        
        // Simplified validation - if we get an object with properties, it's valid
        const isValidResult = searchResults && 
          typeof searchResults === 'object' && 
          searchResults !== null &&
          (searchResults.summary || searchResults.details || Object.keys(searchResults).length > 0);
        
        if (isValidResult) {
          console.log('✅ Valid results confirmed, caching and navigating');
          
          // Cache the fresh results
          setLoadingMessage('Processing results...');
          
          try {
            if ('cacheSearchResult' in searchCache) {
              (searchCache as any).cacheSearchResult(searchQuery.toLowerCase(), searchResults);
            } else if ('set' in searchCache) {
              (searchCache as any).set(searchQuery.toLowerCase(), searchResults);
            } else if ('cache' in searchCache) {
              (searchCache as any).cache(searchQuery.toLowerCase(), searchResults);
            }
            console.log('✅ Results cached successfully');
          } catch (cacheError) {
            console.warn('⚠️ Failed to cache result:', cacheError);
          }
          
          setLoadingMessage('Results ready!');
          await new Promise(resolve => setTimeout(resolve, 500)); // Slightly longer pause to ensure everything is ready
          
          setIsLoading(false);
          console.log('🚀 Navigating to results page with fresh data');
          navigate(`/results?q=${encodeURIComponent(searchQuery)}`);
          return;
        } else {
          console.log('❌ Invalid results received:', searchResults);
          throw new Error('Invalid results from API');
        }
        
      } catch (apiError) {
        console.error('❌ Health search service failed:', apiError);
        setLoadingMessage('No results found. Please try a different search.');
        
        setTimeout(() => {
          setIsLoading(false);
          setLoadingMessage('Searching...');
        }, 3000);
        return;
      }
        
    } catch (error) {
      console.error("❌ Search error:", error);
      setLoadingMessage('Search failed. Please try again.');
      
      setTimeout(() => {
        setIsLoading(false);
        setLoadingMessage('Searching...');
      }, 2000);
    }
  };

  const handleAnalysisComplete = async (foodName: string): Promise<void> => {
    if (!foodName || isLoading) {
      setIsUploadModalOpen(false);
      return;
    }

    setIsLoading(true);
    setLoadingMessage('Processing image analysis...');
    
    try {
      console.log("Image analysis completed, food detected:", foodName);
      setSearchQuery(foodName);
      
      setLoadingMessage('Checking nutrition database...');
      const cachedNutrition = searchCache.getCachedResult(foodName.toLowerCase());
      
      if (cachedNutrition && cachedNutrition.trim().length > 0) {
        setLoadingMessage('Found nutrition data!');
        await new Promise(resolve => setTimeout(resolve, 300));
        
        setIsLoading(false);
        navigate(`/results?q=${encodeURIComponent(foodName)}`);
        return;
      }

      setLoadingMessage('Analyzing nutrition content...');
      
      try {
        const nutritionResults = await healthSearchService.searchHealthInfo(foodName);
        
        const isValidResult = nutritionResults && 
          typeof nutritionResults === 'object' && 
          nutritionResults !== null &&
          (nutritionResults.summary || nutritionResults.details || Object.keys(nutritionResults).length > 0);
        
        if (isValidResult) {
          setLoadingMessage('Processing nutrition data...');
          
          // Cache the results
          try {
            if ('cacheSearchResult' in searchCache) {
              (searchCache as any).cacheSearchResult(foodName.toLowerCase(), nutritionResults);
            } else if ('set' in searchCache) {
              (searchCache as any).set(foodName.toLowerCase(), nutritionResults);
            } else if ('cache' in searchCache) {
              (searchCache as any).cache(foodName.toLowerCase(), nutritionResults);
            }
          } catch (cacheError) {
            console.warn('Failed to cache nutrition result:', cacheError);
          }
          
          setLoadingMessage('Nutrition analysis complete!');
          await new Promise(resolve => setTimeout(resolve, 300));
          
          setIsLoading(false);
          navigate(`/results?q=${encodeURIComponent(foodName)}`);
          return;
        } else {
          throw new Error('Invalid nutrition results');
        }
        
      } catch (nutritionError) {
        console.error('Nutrition analysis failed:', nutritionError);
        setLoadingMessage('Could not analyze this food item. Please try again.');
        
        setTimeout(() => {
          setIsLoading(false);
          setLoadingMessage('Searching...');
        }, 3000);
        return;
      }
      
    } catch (error) {
      console.error("Error processing analyzed food:", error);
      setLoadingMessage('Analysis failed. Please try again.');
      
      setTimeout(() => {
        setIsLoading(false);
        setLoadingMessage('Searching...');
      }, 2000);
    } finally {
      setIsUploadModalOpen(false);
    }
  };

  // Reset loading state on component unmount
  useEffect(() => {
    return () => {
      setIsLoading(false);
      setLoadingMessage('Searching...');
    };
  }, []);

  const LoadingOverlay: React.FC<{ message: string; progress: number }> = ({ message, progress }) => (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 9999
    }}>
      <div style={{
        backgroundColor: 'white',
        borderRadius: '12px',
        padding: '40px',
        textAlign: 'center',
        minWidth: '320px',
        boxShadow: '0 10px 25px rgba(0, 0, 0, 0.2)'
      }}>
        {/* Spinner */}
        <div style={{
          width: '48px',
          height: '48px',
          border: '4px solid #e5e7eb',
          borderTop: '4px solid #3b82f6',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite',
          margin: '0 auto 24px auto'
        }} />
        
        {/* Title */}
        <h3 style={{
          margin: '0 0 8px 0',
          fontSize: '24px',
          fontWeight: '600',
          color: '#1f2937'
        }}>
          Loading Content
        </h3>
        
        {/* Subtitle */}
        <p style={{
          margin: '0 0 24px 0',
          fontSize: '16px',
          color: '#6b7280'
        }}>
          Please wait while we process your request...
        </p>
        
        {/* Progress Bar */}
        <div style={{
          width: '100%',
          height: '8px',
          backgroundColor: '#e5e7eb',
          borderRadius: '4px',
          overflow: 'hidden',
          marginBottom: '12px'
        }}>
          <div style={{
            width: `${progress}%`,
            height: '100%',
            backgroundColor: '#3b82f6',
            borderRadius: '4px',
            transition: 'width 0.3s ease'
          }} />
        </div>
        
        {/* Progress Text */}
        <p style={{
          margin: 0,
          fontSize: '14px',
          color: '#6b7280'
        }}>
          {progress}% Complete
        </p>
      </div>
    </div>
  );

  return (
    <div className="search-landing">
      {isLoading && (
        <LoadingOverlay 
          message={loadingMessage}
          progress={progressFromMessage(loadingMessage)}
        />
      )}
      
      <MultiAuthLogin 
        position="top-right"
        className="main-login-button"
        onUserChange={handleUserChange}
        onSignIn={handleSignIn}
        onSignOut={handleSignOut}
      />

      <div className="search-container-centered">
        <div className="logo-container">
          {!imageError ? (
            <img 
              src="/assets/293a2ef6-826c-4ec9-9b26-c2abf3bb894f.png"
              alt="What is Healthy?"
              className="search-logo-image"
              onError={() => setImageError(true)}
              onLoad={() => setImageError(false)}
            />
          ) : (
            <h1 className="search-logo">
              What is Healthy?
            </h1>
          )}
        </div>
        
        <div className="search-input-container">
          <form onSubmit={(e) => { 
            e.preventDefault();
            handleSearch();
          }}>
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSearch()}
              placeholder="Ask anything about health..."
              className="search-input"
              autoFocus
              disabled={isLoading}
            />
          </form>

          <div className="search-icons">
            {searchQuery && !isLoading && (
              <button
                type="button"
                onClick={() => setSearchQuery('')}
                className="icon-button clear-button"
                aria-label="Clear all"
              >
                <svg viewBox="0 0 24 24" fill="currentColor">
                  <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
                </svg>
              </button>
            )}
            
            <button
              type="button"
              onClick={() => setIsUploadModalOpen(true)}
              className="icon-button"
              aria-label="Upload image"
              disabled={isLoading}
            >
              <svg viewBox="0 0 24 24" fill="currentColor">
                <path d="M9 2L7.17 4H4c-1.1 0-2 .9-2 2v12c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2h-3.17L15 2H9zm3 15c-2.76 0-5-2.24-5-5s2.24-5 5-5 5 2.24 5 5-2.24 5-5 5z"/>
              </svg>
            </button>
            
            <button
              type="button"
              onClick={() => {
                if (isLoading) return;
                
                if (!('webkitSpeechRecognition' in window)) {
                  alert('Speech recognition not supported in this browser');
                  return;
                }

                if (!recognitionRef.current) {
                  const recognition = new (window as any).webkitSpeechRecognition();
                  recognition.continuous = false;
                  recognition.interimResults = false;
                  recognition.lang = 'en-US';

                  recognition.onstart = () => setIsListening(true);
                  recognition.onresult = (event: any) => {
                    const transcript = event.results[0][0].transcript;
                    setSearchQuery(transcript);
                    setIsListening(false);
                  };
                  recognition.onerror = () => setIsListening(false);
                  recognition.onend = () => setIsListening(false);

                  recognitionRef.current = recognition;
                }

                if (!isListening) {
                  recognitionRef.current.start();
                } else {
                  recognitionRef.current.stop();
                  setIsListening(false);
                }
              }}
              className={`icon-button ${isListening ? 'listening' : ''}`}
              aria-label={isListening ? 'Stop listening' : 'Start voice input'}
              disabled={isLoading}
            >
              <svg viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 14c1.66 0 3-1.34 3-3V5c0-1.66-1.34-3-3-3S9 3.34 9 5v6c0 1.66 1.34 3 3 3z"/>
                <path d="M17 11c0 2.76-2.24 5-5 5s-5-2.24-5-5H5c0 3.53 2.61 6.43 6 6.92V21h2v-3.28c3.39-.49 6-3.3 6-6.72h-2z"/>
              </svg>
            </button>
          </div>
        </div>

        <div className="search-buttons">
          <button 
            onClick={(e) => {
              e.preventDefault();
              handleSearch();
            }}
            className="search-btn primary"
            disabled={!searchQuery.trim() || isLoading}
          >
            {isLoading ? loadingMessage : 'Analyze Nutrition'}
          </button>
          
          <button 
            onClick={() => {
              if (isLoading) return;
              const healthyFoods = ['apple', 'broccoli', 'salmon', 'quinoa', 'blueberries'];
              const randomFood = healthyFoods[Math.floor(Math.random() * healthyFoods.length)];
              setSearchQuery(randomFood);
            }}
            className="search-btn secondary"
            type="button"
            style={{ color: '#000000' }}
            disabled={isLoading}
          >
            I'm Feeling Healthy
          </button>
        </div>
      </div>
      
      <ImageUploadModal
        isOpen={isUploadModalOpen}
        onClose={() => setIsUploadModalOpen(false)}
        onAnalysisComplete={handleAnalysisComplete}
        title="Upload Image"
        subtitle="Upload Image for Analysis"
      />
    </div>
  );
};

export default VHealthSearch;

