import React, { useState, useRef, useEffect } from 'react';

interface Message {
  id: string;
  text: string;
  type: 'user' | 'assistant';
  timestamp: Date;
  context?: string;
}

interface ChatWidgetProps {
  onClose?: () => void;
  initialContext?: string;
}

const ChatWidget: React.FC<ChatWidgetProps> = ({ onClose, initialContext }) => {
  const [isVisible, setIsVisible] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    setIsVisible(true);
    // Add initial greeting message
    const greeting: Message = {
      id: '1',
      text: "Hello! I'm your WiHy health assistant. I can help you understand your health data, nutrition information, and answer questions about your wellness journey. What would you like to know?",
      type: 'assistant',
      timestamp: new Date(),
      context: initialContext
    };
    setMessages([greeting]);
  }, [initialContext]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const formatTime = (date: Date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const getContextualResponse = (userMessage: string, context?: string): string => {
    const lowerMessage = userMessage.toLowerCase();
    
    // Context-specific responses
    if (context === 'nutrition') {
      if (lowerMessage.includes('calorie') || lowerMessage.includes('nutrition')) {
        return "Based on your nutrition data, I can see your daily caloric intake and nutrient breakdown. Would you like me to explain any specific nutritional values or suggest improvements to your diet?";
      }
      if (lowerMessage.includes('protein') || lowerMessage.includes('carb') || lowerMessage.includes('fat')) {
        return "I can help you understand your macronutrient balance. Your current intake shows the distribution of proteins, carbohydrates, and fats. Would you like specific recommendations for optimizing these ratios?";
      }
    }
    
    if (context === 'activity') {
      if (lowerMessage.includes('step') || lowerMessage.includes('walk') || lowerMessage.includes('exercise')) {
        return "I can see your activity data! Your step count and exercise patterns show your daily movement. Would you like suggestions for increasing activity or understanding your current fitness level?";
      }
    }
    
    if (context === 'health') {
      if (lowerMessage.includes('weight') || lowerMessage.includes('bmi')) {
        return "Your health metrics show important trends. I can help you understand your BMI, weight changes, and how they relate to your overall health goals. What specific aspect would you like to discuss?";
      }
    }

    // General health responses
    if (lowerMessage.includes('help') || lowerMessage.includes('what can you do')) {
      return "I can help you with:\n• Understanding your nutrition data and food analysis\n• Explaining health metrics and trends\n• Providing wellness tips and recommendations\n• Answering questions about your health journey\n• Suggesting improvements to your diet and lifestyle\n\nWhat would you like to explore?";
    }
    
    if (lowerMessage.includes('nutrition') || lowerMessage.includes('food') || lowerMessage.includes('diet')) {
      return "I can analyze your nutrition data and help you understand your dietary patterns. I can explain calories, macronutrients, vitamins, and suggest improvements. What specific nutrition question do you have?";
    }
    
    if (lowerMessage.includes('health') || lowerMessage.includes('wellness')) {
      return "Your health and wellness are important! I can help you understand your health metrics, track progress, and provide evidence-based recommendations. What health topic interests you most?";
    }
    
    if (lowerMessage.includes('recommend') || lowerMessage.includes('suggest') || lowerMessage.includes('advice')) {
      return "I'd be happy to provide personalized recommendations! Based on your data, I can suggest improvements for nutrition, activity, or overall wellness. What area would you like recommendations for?";
    }
    
    if (lowerMessage.includes('data') || lowerMessage.includes('track') || lowerMessage.includes('progress')) {
      return "Your health data tells a story! I can help you interpret trends, understand patterns, and track your progress over time. Which data points would you like to explore?";
    }

    // Default responses
    const defaultResponses = [
      "That's an interesting question! Could you tell me more about what specific aspect of your health or nutrition you'd like to explore?",
      "I'm here to help with your health journey. Could you provide more details so I can give you the best assistance?",
      "Great question! I can provide more targeted help if you let me know what specific health or nutrition information you're looking for.",
      "I'd love to help you with that. Could you be more specific about what aspect of your wellness you'd like to discuss?"
    ];
    
    return defaultResponses[Math.floor(Math.random() * defaultResponses.length)];
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      text: inputMessage,
      type: 'user',
      timestamp: new Date(),
      context: initialContext
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');
    setIsTyping(true);

    // Simulate AI response delay
    setTimeout(() => {
      const assistantMessage: Message = {
        id: (Date.now() + 1).toString(),
        text: getContextualResponse(inputMessage, initialContext),
        type: 'assistant',
        timestamp: new Date(),
        context: initialContext
      };
      
      setMessages(prev => [...prev, assistantMessage]);
      setIsTyping(false);
    }, 1000 + Math.random() * 1000);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleClose = () => {
    setIsVisible(false);
    setTimeout(() => {
      onClose?.();
    }, 300);
  };

  return (
    <div style={{
      position: 'fixed',
      bottom: '20px',
      right: '20px',
      width: '360px',
      height: '500px',
      backgroundColor: 'white',
      borderRadius: '16px',
      boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)',
      border: '1px solid #e5e7eb',
      display: 'flex',
      flexDirection: 'column',
      overflow: 'hidden',
      zIndex: 1000,
      transform: isVisible ? 'translateY(0) scale(1)' : 'translateY(100%) scale(0.95)',
      opacity: isVisible ? 1 : 0,
      transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif'
    }}>
      {/* Header */}
      <div style={{
        padding: '16px 20px',
        backgroundColor: '#1a73e8',
        color: 'white',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between'
      }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '12px' }}>
          <div style={{
            width: '32px',
            height: '32px',
            borderRadius: '50%',
            backgroundColor: 'rgba(255, 255, 255, 0.2)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '14px',
            fontWeight: 'bold'
          }}>
            WH
          </div>
          <div>
            <div style={{ fontSize: '16px', fontWeight: '600' }}>WiHy Assistant</div>
            <div style={{ fontSize: '12px', opacity: 0.9 }}>Your health companion</div>
          </div>
        </div>
        <button
          onClick={handleClose}
          style={{
            background: 'none',
            border: 'none',
            color: 'white',
            cursor: 'pointer',
            padding: '4px',
            borderRadius: '4px',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            opacity: 0.8,
            transition: 'opacity 0.2s ease'
          }}
          onMouseEnter={(e) => e.currentTarget.style.opacity = '1'}
          onMouseLeave={(e) => e.currentTarget.style.opacity = '0.8'}
        >
          <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
            <path d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"/>
          </svg>
        </button>
      </div>

      {/* Messages */}
      <div style={{
        flex: 1,
        overflowY: 'auto',
        padding: '16px',
        display: 'flex',
        flexDirection: 'column',
        gap: '16px'
      }}>
        {messages.map((message) => (
          <div key={message.id} style={{
            display: 'flex',
            flexDirection: message.type === 'user' ? 'row-reverse' : 'row',
            gap: '8px',
            alignItems: 'flex-start'
          }}>
            {message.type === 'assistant' && (
              <div style={{
                width: '28px',
                height: '28px',
                borderRadius: '50%',
                backgroundColor: '#1a73e8',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                color: 'white',
                fontSize: '12px',
                fontWeight: 'bold',
                flexShrink: 0
              }}>
                WH
              </div>
            )}
            <div style={{
              maxWidth: '75%',
              display: 'flex',
              flexDirection: 'column',
              gap: '4px'
            }}>
              <div style={{
                backgroundColor: message.type === 'user' ? '#1a73e8' : '#f3f4f6',
                color: message.type === 'user' ? 'white' : '#374151',
                padding: '12px 16px',
                borderRadius: message.type === 'user' ? '18px 18px 4px 18px' : '18px 18px 18px 4px',
                fontSize: '14px',
                lineHeight: '1.4',
                whiteSpace: 'pre-wrap'
              }}>
                {message.text}
              </div>
              <div style={{
                fontSize: '11px',
                color: '#9ca3af',
                textAlign: message.type === 'user' ? 'right' : 'left',
                paddingLeft: message.type === 'user' ? '0' : '16px',
                paddingRight: message.type === 'user' ? '16px' : '0'
              }}>
                {formatTime(message.timestamp)}
                {message.context && (
                  <span style={{ marginLeft: '8px', fontStyle: 'italic' }}>
                    • viewing {message.context}
                  </span>
                )}
              </div>
            </div>
          </div>
        ))}
        
        {isTyping && (
          <div style={{
            display: 'flex',
            gap: '8px',
            alignItems: 'flex-start'
          }}>
            <div style={{
              width: '28px',
              height: '28px',
              borderRadius: '50%',
              backgroundColor: '#1a73e8',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '12px',
              fontWeight: 'bold'
            }}>
              WH
            </div>
            <div style={{
              backgroundColor: '#f3f4f6',
              padding: '12px 16px',
              borderRadius: '18px 18px 18px 4px',
              display: 'flex',
              gap: '4px',
              alignItems: 'center'
            }}>
              <div style={{
                width: '6px',
                height: '6px',
                borderRadius: '50%',
                backgroundColor: '#9ca3af',
                animation: 'typing 1.5s ease-in-out infinite'
              }} />
              <div style={{
                width: '6px',
                height: '6px',
                borderRadius: '50%',
                backgroundColor: '#9ca3af',
                animation: 'typing 1.5s ease-in-out infinite 0.2s'
              }} />
              <div style={{
                width: '6px',
                height: '6px',
                borderRadius: '50%',
                backgroundColor: '#9ca3af',
                animation: 'typing 1.5s ease-in-out infinite 0.4s'
              }} />
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Input */}
      <div style={{
        padding: '16px',
        borderTop: '1px solid #f3f4f6',
        backgroundColor: '#fafbfc'
      }}>
        <div style={{
          display: 'flex',
          gap: '8px',
          alignItems: 'flex-end'
        }}>
          <textarea
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask about your health data..."
            style={{
              flex: 1,
              border: '1px solid #d1d5db',
              borderRadius: '12px',
              padding: '12px 16px',
              fontSize: '14px',
              resize: 'none',
              minHeight: '20px',
              maxHeight: '80px',
              fontFamily: 'inherit',
              outline: 'none'
            }}
            rows={1}
          />
          <button
            onClick={handleSendMessage}
            disabled={!inputMessage.trim() || isTyping}
            style={{
              backgroundColor: inputMessage.trim() ? '#1a73e8' : '#d1d5db',
              color: 'white',
              border: 'none',
              borderRadius: '12px',
              padding: '12px',
              cursor: inputMessage.trim() ? 'pointer' : 'not-allowed',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              transition: 'background-color 0.2s ease'
            }}
          >
            <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
              <path d="M2.01 21L23 12 2.01 3 2 10l15 2-15 2z"/>
            </svg>
          </button>
        </div>
      </div>
      
      <style>{`
        @keyframes typing {
          0%, 60%, 100% {
            transform: translateY(0);
            opacity: 0.4;
          }
          30% {
            transform: translateY(-10px);
            opacity: 1;
          }
        }
      `}</style>
    </div>
  );
};

export default ChatWidget;
