import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useSearchParams, useNavigate } from 'react-router-dom';
import VHealthSearch from './VHealthSearch';
import SearchResults from './SearchResults';
import openaiAPI from './services/openaiAPI';
import './VHealthSearch.css';

// Define a type for the allowed data sources that matches SearchResults requirements
type AllowedDataSource = "error" | "openai" | "local" | "vnutrition";

// Results page wrapper to handle URL parameters
const ResultsPage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const query = searchParams.get('q') || '';
  const [isLoading, setIsLoading] = useState(true);
  const [results, setResults] = useState<string>('');
  const [dataSource, setDataSource] = useState<AllowedDataSource>("local");
  const [citations, setCitations] = useState<string[]>([]);
  const [recommendations, setRecommendations] = useState<string[]>([]);
  const [disclaimer, setDisclaimer] = useState<string>('');
  
  // Fetch results when query changes
  useEffect(() => {
    const fetchResults = async () => {
      if (!query) {
        setIsLoading(false);
        return;
      }
      
      console.log("Fetching results for query:", query);
      setIsLoading(true);
      
      try {
        // Try to use openaiAPI
        const result = await openaiAPI.searchHealthInfo(query);
        console.log("Search result:", result);
        
        setResults(result.details);
        setDataSource("openai");
        
        if (result.sources) {
          setCitations(result.sources);
        }
        
        if (result.recommendations) {
          setRecommendations(result.recommendations);
        }
        
        if (result.medicalDisclaimer) {
          setDisclaimer(result.medicalDisclaimer);
        }
      } catch (error) {
        console.error("Error fetching results:", error);
        setResults(`Sorry, we couldn't retrieve information about "${query}". Please try a different search or try again later.`);
        setDataSource("error");
      } finally {
        setIsLoading(false);
      }
    };
    
    fetchResults();
  }, [query]);

  const handleBackToSearch = () => {
    navigate('/');
  };

  const handleNewSearch = (newQuery: string) => {
    navigate(`/results?q=${encodeURIComponent(newQuery)}`);
  };

  const handleSearch = async (query: string) => {
    setIsLoading(true);
    // setQuery(query); // This line seems to be missing in your code, assuming it's needed
  
    try {
      console.log('üîç Starting search for:', query);
      
      // Always try database first for food queries
      const foodKeywords = [
        'quinoa', 'broccoli', 'apple', 'tomato', 'banana', 'food', 'nutrition'
      ];
      
      const isFoodQuery = foodKeywords.some(keyword => 
        query.toLowerCase().includes(keyword.toLowerCase())
      );
      
      console.log('üçé Is food query?', isFoodQuery);
      
      if (isFoodQuery) {
        try {
          console.log('üîç Searching database for:', query);
          // Assuming searchFoodDatabase is imported or available in this scope
          const foodResult = await searchFoodDatabase(query);
          
          console.log('üìä Database result:', foodResult);
          
          if (foodResult.found) {
            console.log('‚úÖ Found in database - using database data');
            setResults(JSON.stringify(foodResult, null, 2));
            setDataSource('vnutrition'); // Important: Set correct source
            setIsLoading(false);
            return; // Exit early - don't fall back to OpenAI
          }
        } catch (error) {
          console.error('‚ùå Database search failed:', error);
          // Continue to fallback
        }
      }
      
      // Fallback to OpenAI
      console.log('ü§ñ Using OpenAI fallback');
      const generalResult = await openaiAPI.searchHealthInfo(query);
      setResults(generalResult);
      setDataSource('openai');
      
    } catch (error) {
      console.error('Search error:', error);
      setResults('Error searching. Please try again.');
      setDataSource('error');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <SearchResults
      query={query}
      results={results}
      onBackToSearch={handleBackToSearch}
      onNewSearch={handleNewSearch}
      isLoading={isLoading}
      dataSource={dataSource}
      citations={citations}
      recommendations={recommendations}
      disclaimer={disclaimer}
    />
  );
};

// Main App with routing
const App: React.FC = () => {
  console.log("App component rendered");
  
  return (
    <Router>
      <Routes>
        <Route path="/" element={<VHealthSearch />} />
        <Route path="/results" element={<ResultsPage />} />
      </Routes>
    </Router>
  );
};

export default App;
