import React, { useState, useRef, useEffect } from 'react';
import { wihyAPI } from '../services/wihyAPI';

interface ChatMessage {
  id: string;
  type: 'user' | 'ai';
  content: string;
  timestamp: Date;
}

interface ChatWidgetProps {
  isOpen: boolean;
  onClose: () => void;
  searchQuery?: string;
  searchResponse?: string;
  currentContext?: string; // Current dashboard section being viewed
  inline?: boolean; // Whether to render inline or as fixed overlay
}

const ChatWidget: React.FC<ChatWidgetProps> = ({ isOpen, onClose, searchQuery, searchResponse, currentContext, inline = false }) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  // Auto-scroll to bottom when new messages are added
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Initialize conversation with search results if provided
  useEffect(() => {
    if (searchQuery && searchResponse && messages.length === 0) {
      const initialMessages: ChatMessage[] = [
        {
          id: Date.now().toString() + '-user',
          type: 'user',
          content: searchQuery,
          timestamp: new Date()
        },
        {
          id: Date.now().toString() + '-ai',
          type: 'ai',
          content: searchResponse,
          timestamp: new Date()
        }
      ];
      setMessages(initialMessages);
    }
  }, [searchQuery, searchResponse, messages.length]);

  // Focus input when chat opens
  useEffect(() => {
    if (isOpen && inputRef.current) {
      setTimeout(() => inputRef.current?.focus(), 100);
    }
  }, [isOpen]);

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage: ChatMessage = {
      id: Date.now().toString() + '-user',
      type: 'user',
      content: inputValue.trim(),
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    try {
      // Build conversation context from previous messages
      const conversationHistory = messages.slice(-6) // Last 6 messages for context
        .map(msg => `${msg.type === 'user' ? 'User' : 'AI'}: ${msg.content}`)
        .join('\n');
      
      const contextualQuery = conversationHistory 
        ? `Previous conversation:\n${conversationHistory}\n\nCurrent question: ${userMessage.content}`
        : userMessage.content;

      const response = await wihyAPI.askAnything({
        query: contextualQuery,
        user_context: {
          conversation_context: conversationHistory,
          current_context: currentContext,
          is_followup: messages.length > 0
        }
      });

      const aiResponse = wihyAPI.formatWihyResponse(response);
      
      const aiMessage: ChatMessage = {
        id: Date.now().toString() + '-ai',
        type: 'ai',
        content: aiResponse,
        timestamp: new Date()
      };

      setMessages(prev => [...prev, aiMessage]);
    } catch (error) {
      console.error('Chat error:', error);
      const errorMessage: ChatMessage = {
        id: Date.now().toString() + '-ai',
        type: 'ai',
        content: "I'm having trouble connecting right now. Please try again in a moment.",
        timestamp: new Date()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };
  
  if (!isOpen) {
    return null;
  }

  return (
    <div 
      className="chat-widget"
      style={{
        position: inline ? 'relative' : 'fixed',
        left: inline ? 'auto' : '0',
        top: inline ? 'auto' : '0',
        width: inline ? '100%' : '350px',
        height: inline ? '100%' : '100vh',
        backgroundColor: 'white',
        borderRight: inline ? 'none' : '1px solid #e5e7eb',
        border: inline ? '1px solid #e5e7eb' : 'none',
        borderRadius: inline ? '8px' : '0',
        display: 'flex',
        flexDirection: 'column',
        zIndex: inline ? 'auto' : 1000,
        fontFamily: 'system-ui, -apple-system, sans-serif',
        animation: inline ? 'none' : 'slideInFromLeft 0.3s ease-out',
        boxShadow: inline ? 'none' : '4px 0 12px rgba(0, 0, 0, 0.1)'
      }}
    >
      {/* Chat Header */}
      <div style={{
        padding: '16px 20px',
        borderBottom: '1px solid #e5e7eb',
        backgroundColor: '#f8fafc',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <div>
          <h3 style={{
            margin: 0,
            fontSize: '16px',
            fontWeight: '600',
            color: '#1e293b'
          }}>
            WiHy Health Chat
          </h3>
          <p style={{
            margin: 0,
            fontSize: '12px',
            color: '#64748b'
          }}>
            Ask follow-up questions to learn more
          </p>
        </div>
        {!inline && (
          <button
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '20px',
              cursor: 'pointer',
              color: '#64748b',
              padding: '4px'
            }}
          >
            ✕
          </button>
        )}
      </div>

      {/* Messages Area */}
      <div style={{
        flex: 1,
        overflow: 'auto',
        padding: '16px',
        display: 'flex',
        flexDirection: 'column',
        gap: '16px'
      }}>
        {messages.length === 0 ? (
          <div style={{
            textAlign: 'center',
            padding: '40px 20px',
            color: '#64748b'
          }}>
            <div style={{
              fontSize: '48px',
              marginBottom: '16px',
              opacity: 0.3
            }}>
              💬
            </div>
            <div style={{
              fontSize: '14px',
              fontWeight: '500',
              marginBottom: '8px'
            }}>
              Start a health conversation
            </div>
            <div style={{
              fontSize: '12px',
              color: '#94a3b8'
            }}>
              Ask any health question to begin learning
            </div>
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              style={{
                display: 'flex',
                justifyContent: message.type === 'user' ? 'flex-end' : 'flex-start',
                marginBottom: '8px'
              }}
            >
              <div
                style={{
                  maxWidth: '80%',
                  padding: '12px 16px',
                  borderRadius: '18px',
                  backgroundColor: message.type === 'user' ? '#3b82f6' : '#f1f5f9',
                  color: message.type === 'user' ? 'white' : '#334155',
                  fontSize: '14px',
                  lineHeight: '1.5',
                  whiteSpace: 'pre-wrap',
                  wordWrap: 'break-word'
                }}
              >
                {message.content}
              </div>
            </div>
          ))
        )}
        
        {isLoading && (
          <div style={{ display: 'flex', justifyContent: 'flex-start' }}>
            <div style={{
              padding: '12px 16px',
              borderRadius: '18px',
              backgroundColor: '#f1f5f9',
              color: '#64748b',
              fontSize: '14px'
            }}>
              <div style={{
                display: 'flex',
                gap: '4px',
                alignItems: 'center'
              }}>
                <span>Thinking</span>
                <div style={{
                  display: 'inline-flex',
                  gap: '2px'
                }}>
                  <div style={{
                    width: '4px',
                    height: '4px',
                    borderRadius: '50%',
                    backgroundColor: '#64748b',
                    animation: 'pulse 1.4s ease-in-out infinite both',
                    animationDelay: '0s'
                  }}></div>
                  <div style={{
                    width: '4px',
                    height: '4px',
                    borderRadius: '50%',
                    backgroundColor: '#64748b',
                    animation: 'pulse 1.4s ease-in-out infinite both',
                    animationDelay: '0.2s'
                  }}></div>
                  <div style={{
                    width: '4px',
                    height: '4px',
                    borderRadius: '50%',
                    backgroundColor: '#64748b',
                    animation: 'pulse 1.4s ease-in-out infinite both',
                    animationDelay: '0.4s'
                  }}></div>
                </div>
              </div>
            </div>
          </div>
        )}
        
        <div ref={messagesEndRef} />
      </div>

      {/* Input Area */}
      <div style={{
        padding: '16px',
        borderTop: '1px solid #e5e7eb',
        backgroundColor: '#ffffff'
      }}>
        <div style={{
          display: 'flex',
          gap: '8px',
          alignItems: 'flex-end'
        }}>
          <input
            ref={inputRef}
            type="text"
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask a follow-up question..."
            disabled={isLoading}
            style={{
              flex: 1,
              padding: '12px 16px',
              border: '1px solid #d1d5db',
              borderRadius: '20px',
              fontSize: '14px',
              outline: 'none',
              backgroundColor: isLoading ? '#f9fafb' : 'white',
              resize: 'none',
              fontFamily: 'inherit'
            }}
          />
          <button
            onClick={handleSendMessage}
            disabled={!inputValue.trim() || isLoading}
            style={{
              padding: '12px 16px',
              backgroundColor: (!inputValue.trim() || isLoading) ? '#d1d5db' : '#3b82f6',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: (!inputValue.trim() || isLoading) ? 'not-allowed' : 'pointer',
              fontSize: '14px',
              fontWeight: '500',
              transition: 'background-color 0.2s'
            }}
          >
            Send
          </button>
        </div>
      </div>

      {/* CSS animations handled by browser defaults */}
    </div>
  );
};

export default ChatWidget;
