import React, { useState, useEffect, useRef } from 'react';
import { fetchNewsFeed, getArticlesByCategory } from '../services/newsService';
import './HealthNewsFeed.css';

interface NewsArticle {
  id?: string;
  title: string;
  description?: string;
  summary?: string;
  link?: string;
  url?: string;
  publishedDate?: string;
  published_date?: string;
  source?: string;
  category?: string;
  relevanceScore?: number;
  image_url?: string;
  thumbnail?: string;
  imageUrl?: string;
  thumbnailUrl?: string;
  tags?: string[];
}

interface NewsFeedProps {
  maxArticles?: number;
  onAnalyzeArticle?: (query: string) => void;
  setSearchQuery?: (query: string) => void;
  triggerSearch?: () => void;
}

const HealthNewsFeed: React.FC<NewsFeedProps> = ({
  maxArticles = 6,
  onAnalyzeArticle = (query: string) => {
    // Default implementation that will ensure the button always appears
    console.log("Analyze article with query:", query);
  },
  setSearchQuery,
  triggerSearch
}) => {
  // State
  const [articles, setArticles] = useState<NewsArticle[]>([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);
  const [loadingMore, setLoadingMore] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [hasMorePages, setHasMorePages] = useState(true);
  const [analyzingArticle, setAnalyzingArticle] = useState<string | null>(null);
  
  // Refs
  const observerTarget = useRef<HTMLDivElement>(null);
  
  // Categories for the filters
  const categories = [
    { id: 'all', label: 'All Health News' },
    { id: 'Nutrition & Diet', label: 'Nutrition' },
    { id: 'Medical Research', label: 'Medical Research' },
    { id: 'Public Health', label: 'Public Health' },
    { id: 'Clinical Studies', label: 'Clinical Studies' },
    { id: 'Disease Prevention', label: 'Prevention' },
    { id: 'Mental Health', label: 'Mental Health' },
    { id: 'General Health', label: 'General Health' }
  ];

  // Health image placeholders by category
  const placeholders = {
    'Nutrition & Diet': [
      '/assets/placeholders/nutrition1.jpg',
      '/assets/placeholders/nutrition2.jpg',
      '/assets/placeholders/nutrition3.jpg'
    ],
    'Medical Research': [
      '/assets/placeholders/research1.jpg',
      '/assets/placeholders/research2.jpg',
      '/assets/placeholders/research3.jpg'
    ],
    'Public Health': [
      '/assets/placeholders/public1.jpg',
      '/assets/placeholders/public2.jpg',
      '/assets/placeholders/public3.jpg'
    ],
    'Clinical Studies': [
      '/assets/placeholders/clinical1.jpg',
      '/assets/placeholders/clinical2.jpg',
      '/assets/placeholders/clinical3.jpg'
    ],
    'Disease Prevention': [
      '/assets/placeholders/prevention1.jpg',
      '/assets/placeholders/prevention2.jpg',
      '/assets/placeholders/prevention3.jpg'
    ],
    'Mental Health': [
      '/assets/placeholders/mental1.jpg',
      '/assets/placeholders/mental2.jpg',
      '/assets/placeholders/mental3.jpg'
    ],
    'General Health': [
      '/assets/placeholders/general1.jpg',
      '/assets/placeholders/general2.jpg',
      '/assets/placeholders/general3.jpg'
    ],
    'default': [
      '/assets/placeholders/default1.jpg',
      '/assets/placeholders/default2.jpg',
      '/assets/placeholders/default3.jpg'
    ]
  };

  // Fetch news when component mounts or category changes
  useEffect(() => {
    fetchHealthNews();
  }, [selectedCategory]);

  // Set up intersection observer for infinite scrolling
  useEffect(() => {
    if (!observerTarget.current || !hasMorePages) return;

    const observer = new IntersectionObserver(entries => {
      if (entries[0].isIntersecting && !loadingMore && hasMorePages) {
        fetchHealthNews(false);
      }
    }, { threshold: 1.0 });

    observer.observe(observerTarget.current);

    return () => observer.disconnect();
  }, [observerTarget.current, loadingMore, hasMorePages]);

  // Fetch health news from API
  const fetchHealthNews = async (resetPage: boolean = true) => {
    if (resetPage) {
      setLoading(true);
      setCurrentPage(1);
    } else {
      setLoadingMore(true);
      setCurrentPage(prev => prev + 1);
    }

    try {
      const page = resetPage ? 1 : currentPage + 1;
      console.log('Fetching health news for category:', selectedCategory, 'page:', page);
      
      let response;
      
      if (selectedCategory === 'all') {
        response = await fetchNewsFeed([], maxArticles * 2);
      } else {
        response = await getArticlesByCategory(selectedCategory, maxArticles);
      }
      
      if (response.success && response.articles && response.articles.length > 0) {
        let processedArticles = response.articles;
        
        if (resetPage) {
          setArticles(processedArticles);
        } else {
          setArticles(prev => [...prev, ...processedArticles]);
        }
        
        if (response.pagination) {
          setHasMorePages(response.pagination.has_next_page);
        } else {
          setHasMorePages(response.articles.length >= maxArticles);
        }
      } else {
        console.log('No articles found for the selected category on page', page);
        if (resetPage) {
          setArticles([]);
        }
        setHasMorePages(false);
      }
    } catch (error) {
      console.error('Error fetching health news:', error);
      if (resetPage) {
        setArticles([]);
      }
      setHasMorePages(false);
    } finally {
      setLoading(false);
      setLoadingMore(false);
    }
  };

  // Get a random placeholder image based on category
  const getRandomPlaceholderImage = (category?: string) => {
    const categoryKey = category && placeholders[category as keyof typeof placeholders]
      ? category
      : 'default';
      
    const images = placeholders[categoryKey as keyof typeof placeholders];
    const randomIndex = Math.floor(Math.random() * images.length);
    
    return images[randomIndex];
  };

  // Get category color for tags
  const getCategoryColor = (category?: string) => {
    if (!category) return '#94a3b8';
    
    const colors: {[key: string]: string} = {
      'Nutrition & Diet': '#4caf50',
      'Medical Research': '#2196f3',
      'Public Health': '#ff9800',
      'Clinical Studies': '#9c27b0',
      'Disease Prevention': '#f44336',
      'Mental Health': '#03a9f4',
      'General Health': '#ffeb3b',
      'Tech': '#607d8b',
      'Technology': '#607d8b'
    };
    
    return colors[category] || '#94a3b8';
  };

  // Format time ago
  const formatTimeAgo = (dateString?: string) => {
    if (!dateString) return 'Unknown date';
    
    const date = new Date(dateString);
    const now = new Date();
    const diffInSeconds = Math.floor((now.getTime() - date.getTime()) / 1000);
    
    if (diffInSeconds < 60) return 'Just now';
    if (diffInSeconds < 3600) return `${Math.floor(diffInSeconds / 60)} mins ago`;
    if (diffInSeconds < 86400) return `${Math.floor(diffInSeconds / 3600)} hours ago`;
    
    return `${Math.floor(diffInSeconds / 86400)} days ago`;
  };

  // Handle "Analyze with WIHY" button click
  const handleAnalyzeWithWihy = (article: NewsArticle, e: React.MouseEvent) => {
    e.stopPropagation(); // Don't trigger the card click
    e.preventDefault();
    
    if (analyzingArticle === article.id) return;
    
    setAnalyzingArticle(article.id || null);
    
    // Create a search query from the article content
    const searchQuery = `Analyze this health article: ${article.title}. 
Summary: ${article.description || article.summary || 'No description available'}.
Source: ${article.source || 'Unknown source'}
Category: ${article.category || 'Uncategorized'}`;

    // If parent provided setSearchQuery and triggerSearch functions, use them
    if (setSearchQuery && triggerSearch) {
      setSearchQuery(searchQuery);
      
      // Use a small timeout to ensure the query is set before triggering search
      setTimeout(() => {
        triggerSearch();
        setAnalyzingArticle(null);
      }, 100);
    } 
    // Fallback to onAnalyzeArticle if search functions aren't provided
    else if (onAnalyzeArticle) {
      onAnalyzeArticle(searchQuery);
      setTimeout(() => setAnalyzingArticle(null), 3000);
    }
    // No handlers provided, just reset state
    else {
      setTimeout(() => setAnalyzingArticle(null), 1000);
    }
  };

  return (
    <div className="health-news-feed">
      {/* Category selector */}
      <div className="category-selector">
        {categories.map(category => (
          <button
            key={category.id}
            className={`category-button ${selectedCategory === category.id ? 'active' : ''}`}
            onClick={() => setSelectedCategory(category.id)}
          >
            {category.label}
          </button>
        ))}
      </div>

      {/* News grid */}
      {loading ? (
        <div className="loading-spinner">Loading health news...</div>
      ) : (
        <>
          {articles.length === 0 ? (
            <div className="no-articles">
              <p>No articles found for the selected category. Try selecting a different category.</p>
            </div>
          ) : (
            <div className="news-grid">
              {articles.map((article) => (
                <div className="news-card" key={article.id || article.url} onClick={() => {
                  if (article.link || article.url) {
                    window.open(article.link || article.url, '_blank');
                  }
                }}>
                  {/* Image Section */}
                  <div className="news-image">
                    {(article.image_url || article.thumbnail || article.imageUrl || article.thumbnailUrl) ? (
                      <img 
                        src={article.image_url || article.thumbnail || article.imageUrl || article.thumbnailUrl} 
                        alt={article.title}
                        onError={(e) => {
                          console.log('Image failed to load:', article.image_url || article.thumbnail);
                          (e.target as HTMLImageElement).src = getRandomPlaceholderImage(article.category);
                          (e.target as HTMLImageElement).classList.add('placeholder-image');
                        }}
                      />
                    ) : (
                      <img 
                        src={getRandomPlaceholderImage(article.category)}
                        alt={article.title || 'Health article'}
                        className="placeholder-image"
                      />
                    )}
                  </div>
                  
                  {/* Category and date */}
                  <div className="news-meta">
                    <span 
                      className="category-tag"
                      style={{backgroundColor: getCategoryColor(article.category)}}
                    >
                      {article.category || 'Uncategorized'}
                    </span>
                    <span className="news-time">
                      {formatTimeAgo(article.publishedDate || article.published_date)}
                    </span>
                  </div>
                  
                  {/* Content */}
                  <h3 className="news-title">{article.title}</h3>
                  <p className="news-summary">
                    {article.description || article.summary || 'No description available'}
                  </p>
                  
                  <div className="news-footer">
                    {/* Relevance score */}
                    <span className="relevance-score">
                      Relevance: {Math.round((article.relevanceScore || 0) * 100)}%
                    </span>
                    
                    {/* Read full article button */}
                    <a 
                      href={article.url || article.link} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="read-more-btn"
                      onClick={(e) => e.stopPropagation()}
                    >
                      Read Full Article →
                    </a>
                  </div>
                  
                  {/* Analyze with WIHY Button */}
                  {onAnalyzeArticle && (
                    <button 
                      className="analyze-wihy-btn"
                      onClick={(e) => handleAnalyzeWithWihy(article, e)}
                      disabled={analyzingArticle === article.id}
                    >
                      {analyzingArticle === article.id ? (
                        <>
                          <div style={{
                            display: 'inline-block',
                            width: '16px',
                            height: '16px',
                            border: '2px solid #9ca3af',
                            borderRightColor: 'transparent',
                            borderRadius: '50%',
                            animation: 'spin 0.8s linear infinite',
                            marginRight: '4px'
                          }}></div>
                          Analyzing...
                        </>
                      ) : (
                        <>Analyze with WIHY</>
                      )}
                    </button>
                  )}
                </div>
              ))}
            </div>
          )}
          
          {/* Loading more indicator */}
          {loadingMore && (
            <div className="loading-more">
              <div className="loading-spinner"></div>
              <p>Loading more articles...</p>
            </div>
          )}
          
          {/* Intersection Observer Target */}
          {hasMorePages && !loadingMore && articles.length > 0 && (
            <div
              ref={observerTarget}
              className="intersection-observer-target"
              style={{ height: '20px', margin: '20px 0', visibility: 'hidden' }}
            />
          )}
        </>
      )}
    </div>
  );
};

export default HealthNewsFeed;
