import React, { useState, useEffect } from 'react';
import { Camera, Mic } from 'lucide-react';
import './VHealthSearch.css';
import MultiAuthLogin from './components/shared/components/MultiAuthLogin';
import ImageUploadModal from './components/ImageUploadModal';
import HealthNewsFeed from './components/HealthNewsFeed';

// Simple SearchResults component (inline)
interface SearchResultsProps {
  results: any;
}

const SearchResults: React.FC<SearchResultsProps> = ({ results }) => {
  if (!results) return null;
  
  return (
    <div className="search-results-container">
      <h3>Search Results for: "{results.query}"</h3>
      <div className="results-placeholder">
        <p>Results would appear here...</p>
      </div>
    </div>
  );
};

// Simple LoadingOverlay component (inline)
interface LoadingOverlayProps {
  message: string;
  progress: number;
}

const LoadingOverlay: React.FC<LoadingOverlayProps> = ({ message, progress }) => {
  return (
    <div className="loading-overlay">
      <div className="loading-content">
        <div className="loading-spinner"></div>
        <p className="loading-message">{message}</p>
        <div className="loading-progress">
          <div 
            className="loading-progress-bar" 
            style={{ width: `${progress}%` }}
          ></div>
        </div>
      </div>
    </div>
  );
};

const VHealthSearch: React.FC = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [isUploadModalOpen, setIsUploadModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [imageError, setImageError] = useState(false);
  const [showFeelingHealthyContent, setShowFeelingHealthyContent] = useState(false);
  const [showCurrentHealthNews, setShowCurrentHealthNews] = useState(false);
  const [loadingMessage, setLoadingMessage] = useState('Searching...');
  const [searchResults, setSearchResults] = useState<any>(null);

  // Predefined content for "I'm Feeling Healthy" button
  const feelingHealthyContent = {
    title: "You're Feeling Healthy! 🌟",
    subtitle: "Here are some great ways to maintain your wellness journey",
    options: [
      {
        id: 'wellness-tips',
        icon: "💡",
        title: "Wellness Tips",
        description: "Daily health tips to keep you feeling great",
        color: "#10b981"
      },
      {
        id: 'current-news',
        icon: "📰",
        title: "Current Health News",
        description: "Latest health and wellness news from trusted sources", 
        color: "#3b82f6"
      },
      {
        id: 'healthy-recipes',
        icon: "🥗",
        title: "Healthy Recipes",
        description: "Nutritious meal ideas for your healthy lifestyle",
        color: "#f59e0b"
      },
      {
        id: 'exercise-tips',
        icon: "🏃‍♀️",
        title: "Exercise Ideas",
        description: "Simple workouts to keep you active and energized",
        color: "#ef4444"
      }
    ],
    wellnessTips: [
      {
        icon: "🥗",
        title: "Eat Colorful Foods",
        description: "Include a variety of fruits and vegetables in your meals for optimal nutrition."
      },
      {
        icon: "💧",
        title: "Stay Hydrated", 
        description: "Aim for 8 glasses of water daily to keep your body functioning at its best."
      },
      {
        icon: "🏃‍♀️",
        title: "Keep Moving",
        description: "Even 30 minutes of light exercise can boost your energy and mood."
      },
      {
        icon: "😴",
        title: "Prioritize Sleep",
        description: "Quality sleep is essential for recovery and maintaining good health."
      },
      {
        icon: "🧘‍♂️",
        title: "Practice Mindfulness",
        description: "Take a few minutes each day to breathe deeply and center yourself."
      },
      {
        icon: "👥",
        title: "Connect with Others",
        description: "Social connections are vital for mental and emotional well-being."
      }
    ],
    motivationalQuote: "\"Health is not about the weight you lose, but about the life you gain.\" - Dr. Josh Axe"
  };

  const progressFromMessage = (message: string): number => {
    switch (message) {
      case 'Initializing search...': return 10;
      case 'Checking cache...': return 20;
      case 'Loading cached results...': return 60;
      case 'Analyzing with AI...': return 30;
      case 'Caching results...': return 80;
      case 'Results ready!': return 100;
      default: return 0;
    }
  };

  const handleFeelingHealthy = () => {
    setShowFeelingHealthyContent(true);
    setShowCurrentHealthNews(false); // Start with wellness tips, not news
    setTimeout(() => {
      const contentElement = document.getElementById('feeling-healthy-content');
      if (contentElement) {
        contentElement.scrollIntoView({ behavior: 'smooth' });
      }
    }, 100);
  };

  const handleFeelingHealthyOption = (optionId: string) => {
    if (optionId === 'current-news') {
      setShowCurrentHealthNews(true);
    } else {
      setShowCurrentHealthNews(false);
      // Handle other options here if needed
      console.log('Selected option:', optionId);
    }
  };

  const closeFeelingHealthyContent = () => {
    setShowFeelingHealthyContent(false);
    setShowCurrentHealthNews(false);
  };

  const handleSearch = async () => {
    if (!searchQuery.trim()) return;
    setIsLoading(true);
    setLoadingMessage('Searching...');
    
    setTimeout(() => {
      setIsLoading(false);
      setSearchResults({ query: searchQuery, results: [] });
    }, 2000);
  };

  const handleAnalyzeNutrition = () => {
    setSearchQuery('nutrition analysis');
    handleSearch();
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  const handleVoiceSearch = () => {
    console.log('Voice search clicked');
  };

  const handleImageUpload = () => {
    setIsUploadModalOpen(true);
  };

  const handleImageAnalyzed = (foodName: string) => {
    setSearchQuery(foodName);
    setIsUploadModalOpen(false);
    handleSearch();
  };

  const handleUserChange = (user: any) => {
    console.log('User changed:', user);
  };

  const handleSignIn = (user: any) => {
    console.log('User signed in:', user);
  };

  const handleSignOut = () => {
    console.log('User signed out');
  };

  useEffect(() => {
    return () => {
      setIsLoading(false);
      setLoadingMessage('Searching...');
    };
  }, []);

  return (
    <div className="search-landing">
      {/* Loading Overlay */}
      {isLoading && (
        <LoadingOverlay 
          message={loadingMessage}
          progress={progressFromMessage(loadingMessage)}
        />
      )}
      
      {/* Main Header */}
      <div className="main-header">
        <div className="main-login-button">
          <MultiAuthLogin 
            position="top-right"
            onUserChange={handleUserChange}
            onSignIn={handleSignIn}
            onSignOut={handleSignOut}
          />
        </div>
        
        <div className="search-container">
          <div className="logo-container">
            <div className="health-pulse-icon">
              <svg viewBox="0 0 24 24" className="pulse-line">
                <path d="M3 12h2l2-9 4 18 4-9h2l2-9 4 18 1-3h1" 
                      fill="none" 
                      stroke="currentColor" 
                      strokeWidth="2" 
                      strokeLinecap="round" 
                      strokeLinejoin="round"/>
              </svg>
            </div>
            <h1 className="main-title">
              <span className="what-text">W</span>hat is Healthy?
            </h1>
          </div>

          <div className="search-input-container">
            <input
              type="text"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask anything about health..."
              className="search-input"
            />
            
            <div className="input-actions">
              <button
                onClick={handleImageUpload}
                className="action-button camera-button"
                title="Upload Image"
              >
                <Camera size={20} />
              </button>
              
              <button
                onClick={handleVoiceSearch}
                className={`action-button voice-button ${isListening ? 'listening' : ''}`}
                title="Voice Search"
              >
                <Mic size={20} />
              </button>
            </div>
          </div>

          <div className="action-buttons">
            <button 
              onClick={handleAnalyzeNutrition}
              className="action-btn analyze-btn"
              disabled={!searchQuery.trim() || isLoading}
            >
              {isLoading ? loadingMessage : 'Analyze Nutrition'}
            </button>
            
            <button 
              onClick={handleFeelingHealthy}
              className="action-btn feeling-healthy-btn"
            >
              I'm Feeling Healthy
            </button>
          </div>
        </div>
      </div>

      {/* Feeling Healthy Content Section - ONLY PLACE WHERE NEWS CAN APPEAR */}
      {showFeelingHealthyContent && (
        <div id="feeling-healthy-content" className="feeling-healthy-section">
          <div className="feeling-healthy-container">
            <div className="feeling-healthy-header">
              <h2>{feelingHealthyContent.title}</h2>
              <p>{feelingHealthyContent.subtitle}</p>
              <button 
                onClick={closeFeelingHealthyContent}
                className="close-button"
                title="Close"
              >
                ✕
              </button>
            </div>

            {/* Options Grid */}
            <div className="feeling-healthy-options">
              {feelingHealthyContent.options.map((option) => (
                <div 
                  key={option.id} 
                  className={`option-card ${showCurrentHealthNews && option.id === 'current-news' ? 'active' : (!showCurrentHealthNews && option.id === 'wellness-tips' ? 'active' : '')}`}
                  onClick={() => handleFeelingHealthyOption(option.id)}
                  style={{ borderColor: option.color }}
                >
                  <div className="option-icon" style={{ color: option.color }}>
                    {option.icon}
                  </div>
                  <h3 className="option-title">{option.title}</h3>
                  <p className="option-description">{option.description}</p>
                </div>
              ))}
            </div>

            {/* Content Based on Selection - News ONLY shows when Current Health News is selected */}
            {showCurrentHealthNews ? (
              <div className="current-news-section">
                <HealthNewsFeed maxArticles={6} />
              </div>
            ) : (
              <>
                {/* Default Wellness Tips */}
                <div className="wellness-tips-grid">
                  {feelingHealthyContent.wellnessTips.map((tip, index) => (
                    <div key={index} className="wellness-tip-card">
                      <div className="tip-icon">{tip.icon}</div>
                      <h3 className="tip-title">{tip.title}</h3>
                      <p className="tip-description">{tip.description}</p>
                    </div>
                  ))}
                </div>

                <div className="motivational-quote">
                  <p>{feelingHealthyContent.motivationalQuote}</p>
                </div>
              </>
            )}
          </div>
        </div>
      )}

      {/* Search Results - only show when there are actual search results */}
      {searchResults && <SearchResults results={searchResults} />}
      
      {/* Image Upload Modal */}
      <ImageUploadModal
        isOpen={isUploadModalOpen}
        onClose={() => setIsUploadModalOpen(false)}
        onImageSelect={handleImageAnalyzed}
      />

      {/* 
        NO HealthNewsFeed component here! 
        News feed ONLY appears inside the Feeling Healthy section 
        when Current Health News option is specifically selected 
      */}
    </div>
  );
};

export default VHealthSearch;
