import React, { useState, useEffect, useRef } from 'react';
import { chatService } from '../../services/chatService';

interface Message {
  type: 'user' | 'ai';
  message: string;
  timestamp: Date;
}

interface ChatWidgetProps {
  isOpen: boolean;
  onClose: () => void;
  searchQuery?: string;
  searchResponse?: any;
  currentContext?: any;
  inline?: boolean;
  onNewSearch?: (query: string) => void;
  onAddMessage?: (message: Message) => void;
}

const ChatWidget: React.FC<ChatWidgetProps> = ({
  isOpen,
  onClose,
  searchQuery,
  searchResponse,
  currentContext,
  inline = false,
  onNewSearch,
  onAddMessage,
}) => {
  const [conversation, setConversation] = useState<Message[]>([]);
  const [userInput, setUserInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Auto-scroll to bottom when new messages are added
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [conversation, isTyping]);

  // Global message addition capability
  useEffect(() => {
    const addChatMessage = (message: Message) => {
      console.log('🔍 CHATWIDGET: External message addition', message);
      setConversation(prev => [...prev, message]);
    };

    (window as any).addChatMessage = addChatMessage;

    return () => {
      delete (window as any).addChatMessage;
    };
  }, []);

  // Initialize conversation with search context if provided
  useEffect(() => {
    if (isOpen && searchQuery && searchResponse && conversation.length === 0) {
      const initialMessage: Message = {
        type: 'ai',
        message: `I see you searched for "${searchQuery}". I can help you understand these results better or answer follow-up questions!`,
        timestamp: new Date(),
      };
      setConversation([initialMessage]);
    }
  }, [isOpen, searchQuery, searchResponse, conversation.length]);

  const handleSendMessage = async () => {
    if (!userInput.trim()) return;

    const userMessage: Message = {
      type: 'user',
      message: userInput.trim(),
      timestamp: new Date(),
    };

    setConversation(prev => [...prev, userMessage]);
    setUserInput('');
    setIsTyping(true);

    try {
      console.log('🔍 CHATWIDGET: Sending message to chat service');

      const conversationHistory = conversation
        .slice(-3)
        .map(msg => `${msg.type}: ${msg.message}`)
        .join('\n');

      const response = await chatService.sendMessage(
        userMessage.message,
        {
          conversation_history: conversationHistory,
          widget_context: currentContext,
          search_context: searchResponse ? JSON.stringify(searchResponse) : undefined,
        },
        false
      );

      console.log('🔍 CHATWIDGET: Received response', response);

      let aiResponse: string;
      
      if (response && typeof response === 'object') {
        if (response.response) {
          aiResponse = response.response;
        } else if (response.message) {
          aiResponse = response.message;
        } else {
          aiResponse = JSON.stringify(response);
        }
      } else if (typeof response === 'string') {
        aiResponse = response;
      } else {
        aiResponse = 'I apologize, but I encountered an issue processing your request. Could you please try again?';
      }

      const aiMessage: Message = {
        type: 'ai',
        message: aiResponse,
        timestamp: new Date(),
      };

      setConversation(prev => [...prev, aiMessage]);

      if (onAddMessage) {
        onAddMessage(aiMessage);
      }

      console.log('🔍 CHATWIDGET: AI response processed');

    } catch (error) {
      console.error('🔍 CHATWIDGET ERROR: Failed to send message', error);
      
      const errorMessage: Message = {
        type: 'ai',
        message: 'I apologize, but I encountered an error. Please try again or check your connection.',
        timestamp: new Date(),
      };

      setConversation(prev => [...prev, errorMessage]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  if (!isOpen) return null;

  // Render inline version
  if (inline) {
    return (
      <div style={{ 
        background: 'white', 
        border: '1px solid #e0e0e0', 
        borderRadius: '8px', 
        display: 'flex', 
        flexDirection: 'column', 
        height: '400px' 
      }}>
        <div style={{ maxHeight: '300px', overflowY: 'auto', padding: '1rem' }}>
          {conversation.map((msg, index) => (
            <div key={index} style={{ 
              marginBottom: '1rem', 
              display: 'flex', 
              flexDirection: 'column',
              alignItems: msg.type === 'user' ? 'flex-end' : 'flex-start'
            }}>
              <div style={{
                background: msg.type === 'user' ? '#007bff' : '#e9ecef',
                color: msg.type === 'user' ? 'white' : '#333',
                padding: '0.75rem 1rem',
                borderRadius: '18px',
                maxWidth: '70%',
                wordWrap: 'break-word'
              }}>
                {msg.message}
              </div>
            </div>
          ))}
          {isTyping && (
            <div style={{ 
              display: 'flex', 
              alignItems: 'flex-start', 
              marginBottom: '1rem' 
            }}>
              <div style={{
                background: '#e9ecef',
                padding: '0.75rem 1rem',
                borderRadius: '18px'
              }}>
                Typing...
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        
        <div style={{ 
          padding: '1rem', 
          borderTop: '1px solid #e0e0e0', 
          display: 'flex', 
          gap: '0.5rem' 
        }}>
          <input
            type="text"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask a follow-up question..."
            style={{
              flex: 1,
              padding: '0.75rem',
              border: '1px solid #ddd',
              borderRadius: '20px',
              outline: 'none'
            }}
          />
          <button 
            onClick={handleSendMessage}
            style={{
              padding: '0.75rem 1.5rem',
              background: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: 'pointer'
            }}
          >
            Send
          </button>
        </div>
      </div>
    );
  }

  // Render modal version
  return (
    <div style={{
      position: 'fixed',
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      zIndex: 1000
    }}>
      <div style={{
        background: 'white',
        borderRadius: '8px',
        width: '90%',
        maxWidth: '500px',
        height: '70vh',
        display: 'flex',
        flexDirection: 'column',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)'
      }}>
        <div style={{
          padding: '1rem',
          borderBottom: '1px solid #e0e0e0',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <h3 style={{ margin: 0, color: '#333' }}>Health Assistant</h3>
          <button 
            onClick={onClose}
            style={{
              background: 'none',
              border: 'none',
              fontSize: '1.5rem',
              cursor: 'pointer',
              color: '#666'
            }}
          >
            ×
          </button>
        </div>
        
        <div style={{
          flex: 1,
          padding: '1rem',
          overflowY: 'auto'
        }}>
          {conversation.map((msg, index) => (
            <div key={index} style={{ 
              marginBottom: '1rem', 
              display: 'flex', 
              flexDirection: 'column',
              alignItems: msg.type === 'user' ? 'flex-end' : 'flex-start'
            }}>
              <div style={{
                background: msg.type === 'user' ? '#007bff' : '#e9ecef',
                color: msg.type === 'user' ? 'white' : '#333',
                padding: '0.75rem 1rem',
                borderRadius: '18px',
                maxWidth: '70%',
                wordWrap: 'break-word'
              }}>
                {msg.message}
              </div>
              <div style={{ fontSize: '0.75rem', color: '#666', marginTop: '0.25rem' }}>
                {msg.timestamp.toLocaleTimeString()}
              </div>
            </div>
          ))}
          {isTyping && (
            <div style={{ 
              display: 'flex', 
              alignItems: 'flex-start', 
              marginBottom: '1rem' 
            }}>
              <div style={{
                background: '#e9ecef',
                padding: '0.75rem 1rem',
                borderRadius: '18px'
              }}>
                Typing...
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
        
        <div style={{
          padding: '1rem',
          borderTop: '1px solid #e0e0e0',
          display: 'flex',
          gap: '0.5rem'
        }}>
          <input
            type="text"
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask a question..."
            style={{
              flex: 1,
              padding: '0.75rem',
              border: '1px solid #ddd',
              borderRadius: '20px',
              outline: 'none'
            }}
          />
          <button 
            onClick={handleSendMessage}
            disabled={!userInput.trim() || isTyping}
            style={{
              padding: '0.75rem 1.5rem',
              background: (!userInput.trim() || isTyping) ? '#ccc' : '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: (!userInput.trim() || isTyping) ? 'not-allowed' : 'pointer'
            }}
          >
            {isTyping ? 'Sending...' : 'Send'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatWidget;
